// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    //List<Data.NoteItem> notes = vizcore3dx.Note.GetSelectedItems();
    List<Data.NoteItem> notes = vizcore3dx.Note.Notes;
    if (notes.Count == 0) return;

    notes[0].UpdateText("New Note Text!!");
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

public void Example()
{
    int noteId = 5;

    // 해당 리뷰(노트)의 정보 조회
    VIZCore3DX.NET.Data.NoteItem note = vizcore3dx.Review.Note.GetItem(noteId);

    // 해당 리뷰(노트)의 위치 정보 조회
    List<VIZCore3DX.NET.Data.ReviewPosition> positions = note.Position;

    // 이동 거리
    VIZCore3DX.NET.Data.Vertex3D distance = new Data.Vertex3D(100, 200, 0);

    // 마우스가 이동한 거리만큼 리뷰(노트)의 위치를 재설정
    // Note Data : positions[0]
    // Note Position : positions[0].Position
    note.UpdatePosition(positions[0], positions[0].Position + distance);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    float x = 0.0f;
    float y = -45.0f;
    float z = 0.0f;

    // Set Selected Object
    List<VIZCore3DX.NET.Data.Node> nodes =
        vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_PART);

    // Rotate Selected Object
    vizcore3dx.Object3D.Transform.Rotate(nodes, x, y, z);

    // Get Rotation Matrix From Object
    VIZCore3DX.NET.Data.Matrix3D matrix =
        vizcore3dx.Object3D.Transform.GetTransfromRotateAround(nodes, x, y, z);

    List<VIZCore3DX.NET.Data.NoteItem> items = vizcore3dx.Review.Note.Items;

    vizcore3dx.BeginUpdate();

    foreach (VIZCore3DX.NET.Data.NoteItem item in items)
    {
        item.UpdatePosition(matrix);
    }

    vizcore3dx.EndUpdate();
}


