// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    Data.Section section = vizcore3dx.Section.SelectedItem;
    if (section == null) return;

    Data.BoundBox3D box = section.BoundBox;
    if (box == null) return;

    List<Data.Node> items = vizcore3dx.Object3D.FromZone(
        box
        , Data.BoundBoxSearchOption.FullyContained
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    Data.Section section = vizcore3dx.Section.SelectedItem;
    if (section == null) return;

    List<Data.Node> items = section.GetObject3D(Data.BoundBoxSearchOption.FullyContained);
}

private void Example2()
{
    Data.Section section = vizcore3dx.Section.SelectedItem;
    if (section == null) return;

    if (section.SectionType != Manager.SectionManager.SectionTypes.SECTION_BOX) return;

    Data.BoundBox3D box = section.BoundBox;
    if (box == null) return;

    List<Data.Node> items = vizcore3dx.Object3D.FromZone(box, Data.BoundBoxSearchOption.FullyContained);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example1()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Section.Sections.Count == 0) return;
    if (vizcore3dx.Section.SelectedItem == null) return;

    VIZCore3DX.NET.Data.Section item = vizcore3dx.Section.SelectedItem;

    item.ExportVIZX(
        "C:\\Temp\\Section.vizx" /* Path */
        , true                  /* Keep Structure */
        );
}

private void Example2()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Section.Sections.Count == 0) return;
    if (vizcore3dx.Section.SelectedItem == null) return;

    VIZCore3DX.NET.Data.Section item = vizcore3dx.Section.SelectedItem;

    vizcore3dx.Section.ExportVIZX(
        item                        /* Section(Box) */
        , "C:\\Temp\\Section.vizx"   /* Path */
        , true                      /* Keep Structure */
        );
}

private void Example3()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Section.Sections.Count == 0) return;
    if (vizcore3dx.Section.SelectedItem == null) return;

    VIZCore3DX.NET.Data.Section item = vizcore3dx.Section.SelectedItem;

    vizcore3dx.Section.ExportVIZX(
        item.ID                     /* Section(Box) */
        , "C:\\Temp\\Section.vizx"   /* Path */
        , true                      /* Keep Structure */
        );
}


