// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;
private VIZCore3DX.NET.Data.ClashTest clash;

private void Example()
{
   clash = new VIZCore3DX.NET.Data.ClashTest();

    clash.TestKind = VIZCore3DX.NET.Data.ClashTest.ClashTestKind.GROUP_VS_GROUP;

    clash.UseClearanceValue = false;
    clash.ClearanceValue = 3.0f;
    clash.UseRangeValue = false;
    clash.RangeValue = 2.0f;
    clash.PenetrationTolerance = 1.0f;

    List<VIZCore3DX.NET.Data.Node> groupA =
        vizcore3dx.Object3D.Find.QuickSearch(
            new List<string>() { "EQUIPMENT" }
            , false
            , false
            , false
            , false
            , true
            );

    clash.GroupA = groupA;

    List<VIZCore3DX.NET.Data.Node> groupB =
        vizcore3dx.Object3D.Find.QuickSearch(
            new List<string>() { "HULL" }
            , false
            , false
            , false
            , false
            , true
            );

    clash.GroupB = groupB;

    if (clash.ID == -1)
    {
        bool result = vizcore3dx.Clash.Add(clash);

        MessageBox.Show(
            string.Format("ClashTest : {0} / {1}", result == true ? "OK" : "NG", clash.ID)
            , "VIZCore3DX.NET.ClashTest"
            , MessageBoxButtons.OK
            , result == true ? MessageBoxIcon.Information : MessageBoxIcon.Error
            );
    }
    else
    {
        vizcore3dx.Clash.Update(clash);
    }

    vizcore3dx.Clash.OnClashTestFinishedEvent += Clash_OnClashTestFinishedEvent;

    // 간섭검사 수행
    vizcore3dx.Clash.PerformInterferenceCheck(clash.ID);
}

private void Clash_OnClashTestFinishedEvent(object sender, VIZCore3DX.NET.Event.EventManager.ClashEventArgs e)
{
    MessageBox.Show(
        string.Format("Clash Test Completed. : {0} / {1}"
        , e.ID
        , clash.ElapsedTimeString)
        , "VIZCore3DX.NET.ClashTest"
        , MessageBoxButtons.OK, MessageBoxIcon.Information
        );

    bool resultDataKind = true; // True : Assembly, False : Part

    List<VIZCore3DX.NET.Data.ClashTestResultItem> items = null;

    if (clash.TestKind != VIZCore3DX.NET.Data.ClashTest.ClashTestKind.GROUP_VS_MOVING_GROUP)
    {
        items = vizcore3dx.Clash.GetResultItem(
            clash
            , resultDataKind == true
            ? VIZCore3DX.NET.Manager.ClashManager.ResultGroupingOptions.ASSEMBLY
            : VIZCore3DX.NET.Manager.ClashManager.ResultGroupingOptions.PART
            );
    }
    else
    {
        //items = vizcore3dx.Clash.GetResultItem(clash, 0, ckResultAssembly.Checked == true ? VIZCore3DX.NET.Manager.ClashManager.ResultGroupingOptions.ASSEMBLY : VIZCore3DX.NET.Manager.ClashManager.ResultGroupingOptions.PART);
        items = vizcore3dx.Clash.GetResultAllSubItem(
            clash
            , resultDataKind == true
            ? VIZCore3DX.NET.Manager.ClashManager.ResultGroupingOptions.ASSEMBLY
            : VIZCore3DX.NET.Manager.ClashManager.ResultGroupingOptions.PART
            );
    }
}


