// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example1()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Clash.Items.Count == 0) return;

    List<VIZCore3DX.NET.Data.Vertex3D> hotPoint = new List<VIZCore3DX.NET.Data.Vertex3D>();
    hotPoint.Add(new Data.Vertex3D(0, 0, 0));
    hotPoint.Add(new Data.Vertex3D(100, 100, 100));
    hotPoint.Add(new Data.Vertex3D(200, 200, 200));

    List<VIZCore3DX.NET.Data.ClashResultSymbols> symbols = new List<VIZCore3DX.NET.Data.ClashResultSymbols>();
    symbols.Add(VIZCore3DX.NET.Data.ClashResultSymbols.Circle);    // 근접
    symbols.Add(VIZCore3DX.NET.Data.ClashResultSymbols.Square);    // 접촉
    symbols.Add(VIZCore3DX.NET.Data.ClashResultSymbols.Triangle);  // 침투

    vizcore3dx.Clash.ShowResultSymbol(
        hotPoint                        /* Position - Clash Points */
        , symbols                       /* Symbol Kind */ 
        , 10                            /* Radius */
        , true                          /* Fixed Size */
        , System.Drawing.Color.Yellow   /* Color */
        , true                          /* Number Text Visible */
        );
}

private void Example2()
{
    vizcore3dx.Clash.ClearResultSymbol();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // 모델 색상 변경 후, 변경된 색상 조회
    vizcore3dx.Object3D.Color.OnColorChangedEvent += Color_OnColorChangedEvent;

    // 개체 색상 변경 다이얼로그 실행
    // 혹은 사용자가 모델트리의 Context Menu에서 색상 변경 수행
    vizcore3dx.Object3D.Color.ChangeColorDialog();
}

private void Color_OnColorChangedEvent(object sender, Event.EventManager.CustomColorEventArgs e)
{
    StringBuilder sb = new StringBuilder();
    sb.AppendFormat("Event : {0}\n", "OnColorChangedEvent");
    sb.AppendFormat("Node : {0}\n", e.Objects.Count);
    sb.AppendFormat("Color - R:{0}/G:{1}/B:{1}\n", e.CustomColor.R, e.CustomColor.G, e.CustomColor.B);
    System.Diagnostics.Trace.WriteLine(sb.ToString());
}


