private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Disassemble()
{
    vizcore3dx.Object3D.Disassembly.EnableAnimation = true;
    vizcore3dx.Object3D.Disassembly.Distance = 10000;    // 10m
    vizcore3dx.Object3D.Disassembly.SideGap = 1000;      // 1m
    vizcore3dx.Object3D.Disassembly.Target = VIZCore3DX.NET.Manager.DisassemblyManager.TargetKind.ALL;            // All Object
    //vizcore3dx.Object3D.Disassembly.Target = VIZCore3DX.NET.Manager.DisassemblyManager.TargetKind.SELECTED;     // Selected Object

    vizcore3dx.Object3D.Disassembly.Option = VIZCore3DX.NET.Manager.DisassemblyManager.DisassemblyOption.AXIS;    // 축 기준 분해
    //vizcore3dx.Object3D.Disassembly.Option = VIZCore3DX.NET.Manager.DisassemblyManager.DisassemblyOption.LINEAR;// 선형 분해
    //vizcore3dx.Object3D.Disassembly.Option = VIZCore3DX.NET.Manager.DisassemblyManager.DisassemblyOption.SPHERE;// 구 분해

    // If Option is DisassemblyOption.AXIS
    vizcore3dx.Object3D.Disassembly.Axis = VIZCore3DX.NET.Manager.DisassemblyManager.AxisKind.X_PLUS;

    vizcore3dx.Object3D.Disassembly.Disassemble();
}


private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void AddGroup()
{
    List<VIZCore3DX.NET.Data.Node> child =
        vizcore3dx.Object3D.GetChildObject3d(
            0 /* Root Node Index */
            , VIZCore3DX.NET.Data.Object3DChildOption.CHILD_ONLY   /* Sub Node */
            );

    for (int i = 0; i < child.Count; i++)
    {
        bool result = vizcore3dx.Object3D.Disassembly.AddGroup(
            i                   /* ID : 0 ~ */
            , child[i].Index    /* NODE INDEX */
            , true              /* Recursive */
            );
    }
}

private void ClearGroup()
{
    vizcore3dx.Object3D.Disassembly.ClearGroup();
}


private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void AddGroup()
{
    List<VIZCore3DX.NET.Data.Node> child =
        vizcore3dx.Object3D.GetChildObject3d(
            0 /* Root Node Index */
            , VIZCore3DX.NET.Data.Object3DChildOption.CHILD_ONLY   /* Sub Node */
            );

    for (int i = 0; i < child.Count; i++)
    {
        bool result = vizcore3dx.Object3D.Disassembly.AddGroup(
            i                   /* ID : 0 ~ */
            , child[i].Index    /* NODE INDEX */
            , true              /* Recursive */
            );
    }
}

private void ClearGroup()
{
    vizcore3dx.Object3D.Disassembly.ClearGroup();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    AddGroup(0);

    DisassemblyByRate(0.5f);
}

private void AddGroup(int groupKind)
{
    int index = groupKind;

    List<VIZCore3DX.NET.Data.Node> items = null;

    if (index == 0) // Children of Root
    {
        items = vizcore3dx.Object3D.GetChildObject3d(
                    0                                                       /* Root Node Index */
                    , VIZCore3DX.NET.Data.Object3DChildOption.CHILD_ONLY     /* Sub Node */
                    );


    }
    else if (index == 1) // Part (Leaf Node)
    {
        items = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.PART);
    }
    else if (index == 2) // Leaf Assembly
    {
        items = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.LEAF_ASSEMBLY);
    }
    else if (index == 3) // Parent of Leaf Assembly
    {
        items = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.PARENT_LEAF_ASSEMBLY);
    }

    if (items == null) return;

    for (int i = 0; i < items.Count; i++)
    {
        bool result = vizcore3dx.Object3D.Disassembly.AddGroup(
            i                   /* ID : 0 ~ */
            , items[i].Index    /* NODE INDEX */
            , true              /* Recursive */
            );
    }
}

private void DisassemblyByRate(float rate)
{
    vizcore3dx.Object3D.Disassembly.DisassembleBySphereCenterDistanceRate(true, rate);
}


private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Search()
{
    List<string> keyword = new List<string>();
    keyword.Add("PIPE_01_*");
    keyword.Add("PIPE_02_*");
    keyword.Add("PIPE_03_*");
    keyword.Add("PIPE_04_*");

    List<VIZCore3DX.NET.Data.Node> items =
        vizcore3dx.Object3D.Find.QuickSearch(
            keyword
            , false /* Join Condition : True - And, False - Or */
            , true  /* True : Assembly Only, False : Assembly + Part */
            , false /* True : Visible Only, False : All Node */
            , false /* True : Selected Object, False : All Node */
            , false /* True : Full Match, False : Contains */
            , false /* True : Include Node Path, False : Except Node Path */
            );

    if (vizcore3dx.View.XRay.Enable == false)
        vizcore3dx.View.XRay.Enable = true;

    vizcore3dx.View.XRay.SelectionObject3DType = VIZCore3DX.NET.Data.SelectionObject3DTypes.OPAQUE_OBJECT3D;
    vizcore3dx.View.XRay.ColorType = VIZCore3DX.NET.Data.XRayColorTypes.OBJECT_COLOR;

    vizcore3dx.View.XRay.Select(
        items   /* Node */
        , true  /* Selection */
        , true  /* Set Rotation Pivot */
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();

    List<string> strings = new List<string>();
    strings.Add("PIPE");
    List<Data.Node> nodes = vizcore3dx.Object3D.Find.QuickSearch(strings, false, false, false, false, true);
    List<Data.Node> resultNode = vizcore3dx.Object3D.Find.QuickSearchFromBoundBox(nodes[0], Data.Axis.X, 29300, 192, 0, 0, 0);

    vizcore3dx.Object3D.Show(resultNode, false);

    vizcore3dx.EndUpdate();
}


private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void ExportOutside()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    List<VIZCore3DX.NET.Data.Node> items =
        vizcore3dx.Object3D.Find.GetOutsidePart(true);

    string path = "C:\\Model\\Outside.viz";

    bool result = vizcore3dx.Model.ExportVIZ(path, items);

    if (result == false) return;

    VIZCore3DX.NET.Utility.ExplorerHelper.Show(path);
}


