// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    Dictionary<string, List<VIZCore3DX.NET.Data.Node>> items
        = vizcore3dx.Object3D.Find.GetFunctionGroup(
            "898C4327-7A21-464F-BAC8-C0B731351DEE"   /* HHI MBOM 2020 */
            );

    // Case 1)
    foreach (KeyValuePair<string, List<VIZCore3DX.NET.Data.Node>> item in items)
    {
        // Some code here...
    }

    // Case 2)
    List<VIZCore3DX.NET.Data.Node> MACH_PIPE 
        = items.ContainsKey("MACH_PIPE") == true 
        ? items["MACH_PIPE"] 
        : new List<VIZCore3DX.NET.Data.Node>();

    List<VIZCore3DX.NET.Data.Node> HOFT_PIPE
        = items.ContainsKey("HOFT_PIPE") == true
        ? items["HOFT_PIPE"]
        : new List<VIZCore3DX.NET.Data.Node>();

    vizcore3dx.BeginUpdate();

    vizcore3dx.View.XRay.Enable = true;
    vizcore3dx.View.XRay.Clear();

    vizcore3dx.View.XRay.Select(MACH_PIPE, true);
    vizcore3dx.View.XRay.Select(HOFT_PIPE, true);

    vizcore3dx.EndUpdate();
}


