// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<VIZCore3DX.NET.Data.Node> items =
        vizcore3dx.Object3D.FromFilter(
            VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP
            );

    if (items.Count == 0) return;

    vizcore3dx.GeometryUtility.OnOsnapPickingItem += GeometryUtility_OnOsnapPickingItem;

    vizcore3dx.GeometryUtility.ShowOsnap(
        items[0].Index  /* Node Index */
        , false         /* Item : Surface */
        , true          /* Item : Vertex */
        , true          /* Item : Line */
        , true          /* Item : Circle */
        );
}

private void GeometryUtility_OnOsnapPickingItem(object sender, VIZCore3DX.NET.Event.EventManager.OsnapPickingItemEventArgs e)
{
    // 선택한 부재의 인덱스
    int nodeIndex = e.NodeIndex;

    VIZCore3DX.NET.Data.OsnapKind kind = e.Kind;
    VIZCore3DX.NET.Data.Vertex3D point = e.Point;
    VIZCore3DX.NET.Data.Vertex3D start = e.Start;
    VIZCore3DX.NET.Data.Vertex3D end = e.End;
    VIZCore3DX.NET.Data.Vertex3D normal = e.Normal;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
