// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    // 기능 활성화
    vizcore3dx.View.KeyboardBasedTransform.Enable = true;

    // 기준 축 지정
    vizcore3dx.View.KeyboardBasedTransform.Axis = VIZCore3DX.NET.Data.AxisDirection.Z_PLUS;

    // 이동 거리 : 기본, 상세
    vizcore3dx.View.KeyboardBasedTransform.Distance = 1000.0f;
    vizcore3dx.View.KeyboardBasedTransform.DetailDistance = 100.0f;

    // 회전 각도 : 기본, 상세
    vizcore3dx.View.KeyboardBasedTransform.Angle = 90.0f;
    vizcore3dx.View.KeyboardBasedTransform.DetailAngle = 10.0f;

    // 이동/회전 이벤트
    vizcore3dx.View.KeyboardBasedTransform.OnTransformEvent += KeyboardBasedTransform_OnTransformEvent;
}

private void KeyboardBasedTransform_OnTransformEvent(object sender, Event.EventManager.KeyboardBasedObjectTransformEventArgs e)
{
    // 이동
    if(e.Angle == null)
    {
        VIZCore3DX.NET.Data.AxisDirection axis = e.Axis;
        VIZCore3DX.NET.Data.Vector3D distance = e.Distance;
        List<VIZCore3DX.NET.Data.Node> node = e.Node;
    }
    // 회전
    else
    {
        VIZCore3DX.NET.Data.AxisDirection axis = e.Axis;
        VIZCore3DX.NET.Data.Vector3D angle = e.Angle;
        List<VIZCore3DX.NET.Data.Node> node = e.Node;
    }
}


