private void CreateVIZCore3D()
{
    vizcore3dx.OnInitializedVIZCore3DX += VIZCore3DX_OnInitializedVIZCore3DX;
}

private void VIZCore3DX_OnInitializedVIZCore3DX(object sender, EventArgs e)
{
    VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseFile("C:\\Temp\\VIZCore3DX.NET.lic"); 

    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


private void CreateVIZCore3D()
{
    vizcore3dx.OnInitializedVIZCore3DX += VIZCore3DX_OnInitializedVIZCore3DX;
}

private void VIZCore3DX_OnInitializedVIZCore3DX(object sender, EventArgs e)
{
    VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseFile("C:\\Temp\\VIZCore3DX.NET.lic", VIZCore3DX.NET.Data.Products.VIZCore3DdotNET); 

    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


private void CreateVIZCore3D()
{
    vizcore3dx.OnInitializedVIZCore3DX += VIZCore3DX_OnInitializedVIZCore3DX;
}

private void VIZCore3DX_OnInitializedVIZCore3DX(object sender, EventArgs e)
{
    VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseServer("127.0.0.1", 8901);
    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


private void CreateVIZCore3D()
{
    vizcore3dx.OnInitializedVIZCore3DX += VIZCore3DX_OnInitializedVIZCore3DX;
}

private void VIZCore3DX_OnInitializedVIZCore3DX(object sender, EventArgs e)
{
    VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseServer("127.0.0.1", 8901, VIZCore3DX.NET.Data.Products.AUTO);

    //VIZCore3DX.NET.Data.LicenseResults result = vizcore3dx.License.LicenseServer("127.0.0.1", 8901, VIZCore3DX.NET.Data.Products.VIZCORE3DX_NET);

    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();

    // 적용 대상 검색
    List<Data.Node> items = vizcore3dx.Object3D.Find.QuickSearch(
        new List<string>() { "GROUND" }, false, true, false, false, true, false);

    if (items.Count == 0) return;

    // 옵션
    vizcore3dx.ShapeDrawing.DepthTest = true;

    // Material을 적용할 커스텀 객체 생성
    List<int> customBox = new List<int>();
    foreach (Data.Node item in items)
    {
        VIZCore3DX.NET.Data.BoundBox3D boundbox =
                 vizcore3dx.Object3D.GeometryProperty.FromIndex(item.Index).GetBoundBox();

        int id = vizcore3dx.ShapeDrawing.AddBox(
                      boundbox
                      , 1
                      , System.Drawing.Color.Gray
                      , true
                      );

        customBox.Add(id);
    }

    // Material 등록
    string image = "D:\\Ground-Texture.jpg";
    int MaterialID = vizcore3dx.Object3D.Material.Add(image);

    // 커스텀 객체에 Material 설정
    foreach (int item in customBox)
    {
        vizcore3dx.ShapeDrawing.SetMaterial(item, MaterialID);
    }

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();

    Data.BoundBox3D boundbox = vizcore3dx.Model.BoundBox;

    // SEA(바다) 커스텀 개체 추가
    int customSeaId = vizcore3dx.ShapeDrawing.AddSea(
        boundbox.MinZ - 10.0f   /* 높이 : Z축 */
        , true                  /* Visible */
        );

    // 기본 리소스 (SEA Material) 복사
    int material = vizcore3dx.Object3D.Material.Copy(Data.ResourceMaterials.SEA);
    System.Drawing.Color color = System.Drawing.Color.FromArgb(127, 255, 255, 255);

    // 리소스 색상 변경
    vizcore3dx.Object3D.Material.SetColor(material, color);

    // 수정된 리소스 (Material) 를 기존 커스텀 개체에 지정 
    vizcore3dx.ShapeDrawing.SetMaterial(customSeaId, material);

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    vizcore3dx.BeginUpdate();

    vizcore3dx.ShapeDrawing.DepthTest = true;

    int customObjectId =
        vizcore3dx.ShapeDrawing.AddSea(
        vizcore3dx.Model.BoundBox.MinZ   /* Sea Level Height */
        , true                          /* Visible */
        );

    int materialId =
        vizcore3dx.Object3D.Material.Copy(
            VIZCore3DX.NET.Data.ResourceMaterials.SEA
            );

    System.Drawing.Color color = 
        System.Drawing.Color.FromArgb(127, 255, 255, 255);

    vizcore3dx.Object3D.Material.SetColor(
        materialId  /* Material ID */
        , color     /* Meterial Color */
        );

    vizcore3dx.ShapeDrawing.SetMaterial(
        customObjectId  /* Custom Object ID */
        , materialId    /* Meterial ID */
        );

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Add Event
    vizcore3dx.Measure.OnMeasureIntersectionPointEvent += Measure_OnMeasureIntersectionPointEvent;

    bool repeatPickSurface = false;
    bool customMode = true;

    vizcore3dx.Measure.AddIntersectionOfLineAndPlane(
        repeatPickSurface   /* 반복수행모드 */
        , customMode        /* True: 화면에 등록되지 않고, 측정 수행 후 이벤트 발생 / False : 화면에 등록됨 */
        );
}

private void Measure_OnMeasureIntersectionPointEvent(object sender, Event.EventManager.MeasureIntersectionPointEventArgs e)
{
    // 선택한 부재의 인덱스
    int indexLine = e.LineIndex;
    int indexPlane = e.PlaneIndex;

    // 선택 정보
    VIZCore3DX.NET.Data.Vertex3D hitPosition = e.HitPosition;
    VIZCore3DX.NET.Data.Vertex3D lineDirection = e.LineDirection;
    VIZCore3DX.NET.Data.Vertex3D planeNormal = e.PlaneNormal;
}


