// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Add Event
    vizcore3dx.Review.Measure.OnMeasureIntersectionPointEvent += Measure_OnMeasureIntersectionPointEvent;

    bool repeatPickSurface = false;
    bool customMode = true;

    vizcore3dx.Review.Measure.AddIntersectionOfLineAndPlane(
        repeatPickSurface   /* 반복수행모드 */
        , customMode        /* True: 화면에 등록되지 않고, 측정 수행 후 이벤트 발생 / False : 화면에 등록됨 */
        );
}

private void Measure_OnMeasureIntersectionPointEvent(object sender, Event.EventManager.MeasureIntersectionPointEventArgs e)
{
    // 선택한 부재의 인덱스
    int indexLine = e.LineIndex;
    int indexPlane = e.PlaneIndex;

    // 선택 정보
    VIZCore3DX.NET.Data.Vertex3D hitPosition = e.HitPosition;
    VIZCore3DX.NET.Data.Vertex3D lineDirection = e.LineDirection;
    VIZCore3DX.NET.Data.Vertex3D planeNormal = e.PlaneNormal;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // 원점
    VIZCore3DX.NET.Data.Vertex3D BasePosition = 
        new VIZCore3DX.NET.Data.Vertex3D(123951, 1256, 2018);

    // 측정포인트 1
    VIZCore3DX.NET.Data.Vertex3D Position1 = 
        new VIZCore3DX.NET.Data.Vertex3D(143951, 1056, 2018);

    // 측정포인트 2
    VIZCore3DX.NET.Data.Vertex3D Position2 = 
        new VIZCore3DX.NET.Data.Vertex3D(163951, 1256, 2018);

    int id = vizcore3dx.Review.Measure.AddCustom3PointAngle(
        BasePosition                    /* 원점 */
        , Position1                     /* 측정포인트 1 */
        , Position2                     /* 측정포인트 2 */
    );
}


// VIZCore3DX.NET Control
