// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    VIZCore3DX.NET.Data.Node pipe = vizcore3dx.Object3D.FromIndex(4);

    List<VIZCore3DX.NET.Data.Node> cylinder1 = pipe.GetChildObject3d(
        Data.Object3DChildOption.CHILD_ONLY         /* Option */
        , new List<string>() { "Part 1 of PIPE" }   /* Keyword */
        , false     /* Join Condition */
        , false     /* Assembly Only */
        , false     /* Visible Only */
        , false     /* Full Match */
        );

    List<VIZCore3DX.NET.Data.Node> cylinder2 = pipe.GetChildObject3d(
        Data.Object3DChildOption.CHILD_ONLY         /* Option */
        , new List<string>() { "Part 2 of PIPE" }   /* Keyword */
        , false     /* Join Condition */
        , false     /* Assembly Only */
        , false     /* Visible Only */
        , false     /* Full Match */
        );

    if (cylinder1.Count == 0) return;
    if (cylinder2.Count == 0) return;

    int measureId = vizcore3dx.Review.Measure.AddCustomCylinderCylinderAxisCrossPoint(
        cylinder1[0].Index      /* Cylinder Node Index */
        , cylinder2[0].Index    /* Cylinder Node Index */
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<Data.Node> nodes = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);

    List<int> nodesIdx = new List<int>();
    foreach (Data.Node item in nodes)
        nodesIdx.Add(item.Index);

    Data.BoundBoxParameter param = vizcore3dx.Review.Measure.GetBoundBoxByPlaneVertex(
        nodesIdx                        /* Node Index List */
        , new Data.Vector3D(0, 0, 1)    /* Normal */
        );

    // Show Result
    vizcore3dx.ShapeDrawing.AddVertex(
        param.VertexItems /* Vertex List  */
        , 0               /* Group Id */
        , Color.Red       /* Color */
        , 3.0f            /* Radius */
        , 3.0f            /* Size */
        , true            /* Visible */
        );
}

private void Example_DrawBox()
{
    List<Data.Node> nodes = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);

    List<int> nodesIdx = new List<int>();
    foreach (Data.Node item in nodes)
        nodesIdx.Add(item.Index);

    Data.BoundBoxParameter param = vizcore3dx.Review.Measure.GetBoundBoxByPlaneVertex(
        nodesIdx                        /* Node Index List */
        , new Data.Vertex3D(0, 0, 1)    /* Normal */
        );

    vizcore3dx.BeginUpdate();

    // Draw Custom Line
    int shapeId = vizcore3dx.ShapeDrawing.AddLine(
        param.GetShapeDrawingLineParameter()
        , 0
        , Color.Black
        , 5.0f
        , true
        );

    // Use DepthTest
    vizcore3dx.ShapeDrawing.DepthTest = true;

    // Draw Point
    for (int i = 0; i < 8; i++)
    {
        int noteId = vizcore3dx.Review.Note.AddNote3D(i.ToString(), param.VertexItems[i]);

        // Set DepthTest
        vizcore3dx.Review.Note.EnableDepthTest(noteId, true);
    }

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
