// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<int> bodies = new List<int>();
    List<int> coordListNum = new List<int>();
    List<VIZCore3DX.NET.Data.Vertex3D> v = new List<VIZCore3DX.NET.Data.Vertex3D>();
    List<VIZCore3DX.NET.Data.Vector3D> n = new List<VIZCore3DX.NET.Data.Vector3D>();
    List<float> color = new List<float>();

    // 면 #1
    bodies.Add(2296);     // Body Index
    coordListNum.Add(18); // Vertex Float 총 개수

    // Vertex 좌표 => Triangle Mesh 를 기반
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(80180.00, 10600.00, 35572.12));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(71890.00, 10600.00, 35572.12));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(72324.89, 7170.00, 35645.86));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(72324.89, 7170.00, 35645.86));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(80180.00, 7170.00, 35645.86));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(80180.00, 10600.00, 35572.12));

    // Normal 좌표
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));

    // 면의 색상 설정
    color.Add(1.000f); // R
    color.Add(1.000f); // G
    color.Add(0.000f); // B
    color.Add(1);      // A 

    // 면 #2
    bodies.Add(2293);     // Body Index
    coordListNum.Add(18); // Vertex Float 총 개수

    v.Add(new VIZCore3DX.NET.Data.Vertex3D(80180.00, 7170.00, 35645.86));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(72324.89, 7170.00, 35645.86));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(71890.00, 3740.00, 35719.60));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(71890.00, 3740.00, 35719.60));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(80180.00, 3740.00, 35719.60));
    v.Add(new VIZCore3DX.NET.Data.Vertex3D(80180.00, 7170.00, 35645.86));

    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3DX.NET.Data.Vector3D(0.00, 0.00, -1.00));

    color.Add(0.043f); // R
    color.Add(0.043f); // G
    color.Add(0.043f); // B
    color.Add(1);      // A

    vizcore3dx.MeshEdit.ChangeBodyMesh(
        bodies          /* BODY INDEX LIST */
        , v             /* VERTEX LIST */
        , n             /* NORMAL LIST */
        , color         /* COLOR */
        , coordListNum  /* VERTEX COUNT LIST */
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();
    List<int> bodies = new List<int>();
    bodies.Add(1234);    

    for( int j = 0; j < bodies.Count; j++)
    {
        int bodyIndex = bodies[j];
        //Get Vertices Info
        List<VIZCore3DX.NET.Data.BodyVertexData> vertexData = vizcore3dx.MeshEdit.GetBodyVertices(bodyIndex);

        // Vertex Position Move
        for(int i = 0; i < vertexData.Count; i++)
        {
            vertexData[i].Vertex.X += 1000;
        }

        vizcore3dx.MeshEdit.SetBodyVertices(
            bodyIndex /* body index */
            , vertexData /* Vertex Info */
            , false);  /* RebuildData */
    }

    vizcore3dx.Structure.RebuildData();
    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();
    List<int> bodies = new List<int>();
    bodies.Add(1234);    

    for( int j = 0; j < bodies.Count; j++)
    {
        int bodyIndex = bodies[j];
        //Get Vertices Info
        List<VIZCore3DX.NET.Data.BodyVertexData> safeData = vizcore3dx.MeshEdit.GetBodyVertices(bodyIndex);

        // Vertex Postition Move
        for(int i = 0; i < safeData.Count; i++)
        {
            safeData[i].Vertex.X += 1000;
        }

        vizcore3dx.MeshEdit.SetBodyVertices(
            bodyIndex /* body index */
            , safeData /* Vertex Info */
            , false);  /* RebuildData */
    }

    vizcore3dx.Structure.RebuildData();
