// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int id = vizcore3dx.MeshEdit.GetMaxId();
    int parent = vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP)[0].ID;

    List<VIZCore3DX.NET.Data.NewNodeItem> items = new List<VIZCore3DX.NET.Data.NewNodeItem>();

    for (int i = 0; i < 100; i++)
    {
        VIZCore3DX.NET.Data.NewNodeItem item = new VIZCore3DX.NET.Data.NewNodeItem();

        item.Parent = parent;
        item.ID = ++id;
        item.Kind = Data.NodeKind.PART;
        item.Name = string.Format("NAME_{0}", item.ID);

        items.Add(item);
    }

    vizcore3dx.MeshEdit.CreateNodes(items);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int id = vizcore3dx.MeshEdit.GetMaxId();
    int parent = vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP)[0].ID;

    List<VIZCore3DX.NET.Data.NewNodeItem> items = new List<VIZCore3DX.NET.Data.NewNodeItem>();

    for (int i = 0; i < 100; i++)
    {
        VIZCore3DX.NET.Data.NewNodeItem item = new VIZCore3DX.NET.Data.NewNodeItem();

        item.Parent = parent;
        item.ID = ++id;
        item.Kind = Data.NodeKind.PART;
        item.Name = string.Format("NAME_{0}", item.ID);

        items.Add(item);
