    vizcore3dx.Structure.RebuildData();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Parent Node's Index
    int parentIndex = 0;

    // Node Name
    string name = string.Format(
        "NODE #{0}"
        , vizcore3dx.Object3D.GetNodeCount()
        );

    // Create Node
    VIZCore3DX.NET.Data.Node node = 
        vizcore3dx.MeshEdit.CreateNode(
            parentIndex     /* Parent Node's Index */
            , Data.NodeKind.ASSEMBLY /* Node Kind : Assembly, Part */
            , name          /* Node Name */
            );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node assembly = vizcore3dx.MeshEdit.CreateNode(0, VIZCore3DX.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node part = vizcore3dx.MeshEdit.CreateNode(assembly.Index, VIZCore3DX.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3dx.Object3D.GetNodeCount());        

    int id = vizcore3dx.MeshEdit.CreateBody(part.Index, bodyName);

    if (id == -1)
        MessageBox.Show("NG", "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
    else
        MessageBox.Show(string.Format("Body ID : {0}", id), "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Information);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node assembly = vizcore3dx.MeshEdit.CreateNode(0, VIZCore3DX.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node part = vizcore3dx.MeshEdit.CreateNode(assembly.Index, VIZCore3DX.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3dx.Object3D.GetNodeCount());

    List<VIZCore3DX.NET.Data.Vertex3D> coords = new List<VIZCore3DX.NET.Data.Vertex3D>();
    List<VIZCore3DX.NET.Data.Vector3D> normals = new List<VIZCore3DX.NET.Data.Vector3D>();

    coords.Add(new Data.Vertex3D(0.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 0.0, 1000.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(0.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(1000.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(1000.0, 0.0, 0.0));
    coords.Add(new Data.Vertex3D(1000.0, 1000.0, 1000.0));
    coords.Add(new Data.Vertex3D(1000.0, 0.0, 1000.0));
