    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(52, 53, 68));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(52, 67, 68));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(53, 68, 69));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(40, 54, 41));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(41, 55, 56));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(42, 56, 57));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(43, 57, 58));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(44, 58, 59));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(45, 59, 60));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(46, 47, 61));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(53, 60, 69));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(62, 70, 71));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(62, 63, 72));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(63, 64, 73));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(64, 65, 74));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(65, 66, 75));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(66, 67, 76));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(67, 68, 77));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(54, 78, 55));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(61, 62, 70));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(68, 69, 79));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(62, 71, 72));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(63, 72, 73));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(64, 73, 74));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(65, 74, 75));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(66, 75, 76));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(67, 76, 77));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(68, 77, 79));
    triangleIndex.Add(new VIZCore3DX.NET.Data.TriangleIndex(70, 71, 80));            

    vizcore3dx.BeginUpdate();

    int rootIndex = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            rootIndex                               /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    vizcore3dx.MeshEdit.CreateBodyWithIndexedTriangleSet(
        partNode.Index
        , "BODY"
        , vertex
        , triangleIndex
        , Color.Yellow
        );

    vizcore3dx.EndUpdate();

    vizcore3dx.View.ResetView();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";

    SizeF bottom = new SizeF(30, 40);
    SizeF top = new SizeF(10, 20);
    SizeF offset = new SizeF(2.5f, 4.5f);

    float height = 50;

    VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
    matrix.Identity();
    //matrix.SetRotateX(vizcore3dx.View.DegreesToRadians(45.0f));
    //matrix.SetTranslate(1000, 0, 0, false);

    int bodyId = vizcore3dx.MeshEdit.CreateBodyPrimitivePyramid(
            partNode.Index      /* Parent Node Index */
            , bodyName          /* Node Name */
            , bottom            /* Bottom Of Size */
            , top               /* Top Of Size */
            , offset            /* Offset Of Size */
            , height            /* Height */
            , matrix            /* Matrix */
            , Color.Green       /* Color */
            , 1                 /* System Type */
            );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3dx.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3dx.Object3D.GetBodyIndex(bodyId);

    {
        SizeF bottom = new SizeF(30, 40);
        SizeF top = new SizeF(10, 20);
        SizeF offset = new SizeF(2.5f, 4.5f);

        float height = 50;

        VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
        m.Identity();
        //m.SetRotateX(vizcore3dx.View.DegreesToRadians(45.0f));
        //m.SetTranslate(1000, 0, 0, false);

        vizcore3dx.MeshEdit.AddPrimitivePyramid(
            bodyIndex           /* Body Node Index */
            , bottom            /* Bottom Of Size */
            , top               /* Top Of Size */
            , offset            /* Offset Of Size */
            , height            /* Height */
            , matrix            /* Matrix */
            , Color.Green       /* Color */
            , false             /* Rebuild Data */
            , 1                 /* System Type */
            );
    }

    vizcore3dx.Structure.RebuildData();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node assembly = vizcore3dx.MeshEdit.CreateNode(0, VIZCore3DX.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node part = vizcore3dx.MeshEdit.CreateNode(assembly.Index, VIZCore3DX.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3dx.Object3D.GetNodeCount());

    List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

    VIZCore3DX.NET.Data.Matrix3D m = new VIZCore3DX.NET.Data.Matrix3D();
    m.Identity();
    //m.SetRotateX(vizcore3dx.View.DegreesToRadians(45.0f));
    //m.SetTranslate(1000, 0, 0, false);

    vizcore3dx.BeginUpdate();

    int id = vizcore3dx.MeshEdit.CreateBodyPrimitiveBox(
        part.Index          /* Parent (Part) Node Index */
        , bodyName          /* Name */
        , length            /* Axis. Length */
        , m                 /* Matrix */
        , Color.Orange      /* Color */
        );

    vizcore3dx.EndUpdate();

    if (id == -1)
    {
        MessageBox.Show(
            "NG"
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Error
            );
    }
    else
    {
        MessageBox.Show(
            string.Format("Body ID : {0}", id)
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3dx.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3dx.Object3D.GetBodyIndex(bodyId);

    {
        List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

        VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
        matrix.Identity();

        vizcore3dx.MeshEdit.AddPrimitiveBox(
            bodyIndex           /* Body Node Index */
            , length            /* Length */
            , matrix            /* Matrix */
            , Color.Orange      /* Color */
            , false             /* Rebuild Data */
            , 1.0f              /* Scale */
            );
    }

    {
        /* Radius : Top[0], Bottom[1] */
        List<float> radius = new List<float>() { 1000.0f, 1000.0f };

        VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
        matrix.Identity();
        matrix.SetTranslate(1000, 2000, 3000);

        vizcore3dx.MeshEdit.AddPrimitiveCone(
            bodyIndex           /* Body Node Index */
            , radius            /* Radius */
            , 2000.0f           /* Height */
            , matrix            /* Matrix */
            , Color.Blue        /* Color */
            , 12                /* Side Count */
            , 1.0f              /* Scale */
            , false             /* Rebuild Data */
            , 1                 /* System Type */
            );
    }

    vizcore3dx.Structure.RebuildData();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

public void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";

    {
        List<float> radius = new List<float>();
        radius.Add(10); // Inside Radius
        radius.Add(20); // Outside Radius

        float height = 15;
        float angle = 90; // Degree

        int numSection = 4;
        //int numSection = Convert.ToInt32(angle / 10);
        //int numSection = Convert.ToInt32(angle / 15);
        //int numSection = Convert.ToInt32(angle);

        VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
        matrix.Identity();
        //matrix.SetRotateX(vizcore3dx.View.DegreesToRadians(45.0f));
        //matrix.SetTranslate(1000, 0, 0, false);

        vizcore3dx.MeshEdit.CreateBodyPrimitiveRectangularTorus(
            partNode.Index      /* Parent Node Index */
            , bodyName          /* Name */
            , radius            /* Radius */
            , height            /* Height */
            , angle             /* Angle */
            , matrix            /* Matrix */
            , Color.Green       /* Color */
            , numSection        /* Number Of Section */
            , 4                 /* Number of Side (4) */
            , 1.0f              /* Unit Scale (1.0f) */
            , 1                 /* Detail Level (1) */
            );
    }
}


