// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node assembly = vizcore3dx.MeshEdit.CreateNode(0, VIZCore3DX.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node part = vizcore3dx.MeshEdit.CreateNode(assembly.Index, VIZCore3DX.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3dx.Object3D.GetNodeCount());

    VIZCore3DX.NET.Data.Vertex3D start = new VIZCore3DX.NET.Data.Vertex3D(1000.0f, 1000.0f, 1000.0f);
    VIZCore3DX.NET.Data.Vertex3D end = new VIZCore3DX.NET.Data.Vertex3D(0.0f, 0.0f, 0.0f);
    float radius = 500.0f;

    vizcore3dx.BeginUpdate();

    int id = vizcore3dx.MeshEdit.CreateBodyPrimitiveCylinder(
        part.Index          /* Parent (Part) Node Index */
        , bodyName          /* Name */
        , start             /* Start Point */
        , end               /* End Point */
        , radius            /* Radius */
        , Color.Orange      /* Color */
        );

    vizcore3dx.EndUpdate();

    if (id == -1)
    {
        MessageBox.Show(
            "NG"
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Error
            );
    }
    else
    {
        MessageBox.Show(
            string.Format("Body ID : {0}", id)
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node assembly = vizcore3dx.MeshEdit.CreateNode(0, VIZCore3DX.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3dx.Object3D.GetNodeCount());
    VIZCore3DX.NET.Data.Node part = vizcore3dx.MeshEdit.CreateNode(assembly.Index, VIZCore3DX.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3dx.Object3D.GetNodeCount());

    VIZCore3DX.NET.Data.Vertex3D start = new VIZCore3DX.NET.Data.Vertex3D(1000.0f, 1000.0f, 1000.0f);
    VIZCore3DX.NET.Data.Vertex3D end = new VIZCore3DX.NET.Data.Vertex3D(0.0f, 0.0f, 0.0f);
    float radius = 500.0f;

    vizcore3dx.BeginUpdate();

    int id = vizcore3dx.MeshEdit.CreateBodyPrimitiveCylinder(
        part.Index          /* Parent (Part) Node Index */
        , bodyName          /* Name */
        , start             /* Start Point */
        , end               /* End Point */
        , radius            /* Radius */
        , Color.Orange      /* Color */
        , false             /* Rebuild Data */
        );

    vizcore3dx.Structure.RebuildData();

    vizcore3dx.EndUpdate();

    if (id == -1)
    {
        MessageBox.Show(
            "NG"
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Error
            );
    }
    else
    {
        MessageBox.Show(
            string.Format("Body ID : {0}", id)
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3dx.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3dx.Object3D.GetBodyIndex(bodyId);

    {
        List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

        VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
        matrix.Identity();
        //matrix.SetRotateX(vizcore3dx.View.DegreesToRadians(45.0f));
        //matrix.SetTranslate(1000, 0, 0, false);

        vizcore3dx.MeshEdit.AddPrimitiveBox(
            bodyIndex           /* Body Node Index */
            , length            /* Length */
            , matrix            /* Matrix */
            , Color.Orange      /* Color */
            , false             /* Rebuild Data */
            , 1.0f              /* Scale */
            );
    }

    {
        float radius = 1000.0f; // Radius
        float height = 2000.0f; // Height

        VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
        matrix.Identity();
        //matrix.SetRotateX(vizcore3dx.View.DegreesToRadians(45.0f));
        matrix.SetTranslate(1000, 2000, 3000);

        vizcore3dx.MeshEdit.AddPrimitiveCylinder(
            bodyIndex           /* Body Node Index */
            , radius            /* Radius */
            , height            /* Height */
            , matrix            /* Matrix */
            , Color.Blue        /* Color */
            , false             /* Rebuild Data */
            , 12                /* Side Count */
            );
    }

    vizcore3dx.Structure.RebuildData();
}


