// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );


    {
        VIZCore3DX.NET.Data.PolyhedralModel polyhedral = new VIZCore3DX.NET.Data.PolyhedralModel();
        polyhedral.ModelColor = Color.Green;

        {
            VIZCore3DX.NET.Data.PolyhedralContour contour = new VIZCore3DX.NET.Data.PolyhedralContour();
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33873.48354, -5395.02732, 2245.06749), new VIZCore3DX.NET.Data.Vector3D(-0.81781, -0.40693, 0.40693));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33874.31755, -5395.88545, 2245.88546), new VIZCore3DX.NET.Data.Vector3D(-0.81781, -0.40693, 0.40693));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33874.31755, -5387.40017, 2254.37074), new VIZCore3DX.NET.Data.Vector3D(-0.81781, -0.40693, 0.40693));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33873.75403, -5386.82035, 2253.81806), new VIZCore3DX.NET.Data.Vector3D(-0.81781, -0.40693, 0.40693));

            VIZCore3DX.NET.Data.PolyhedralFace face = new VIZCore3DX.NET.Data.PolyhedralFace(contour);
            polyhedral.Face.Add(face);
        }

        {
            VIZCore3DX.NET.Data.PolyhedralContour contour = new VIZCore3DX.NET.Data.PolyhedralContour();
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.11946, -5208.50230, 2075.50000), new VIZCore3DX.NET.Data.Vector3D(0.81781, 0.40693, -0.40693));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.68297, -5209.08212, 2076.05269), new VIZCore3DX.NET.Data.Vector3D(0.81781, 0.40693, -0.40693));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.68297, -5217.56740, 2067.56741), new VIZCore3DX.NET.Data.Vector3D(0.81781, 0.40693, -0.40693));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34231.84897, -5216.70927, 2066.74943), new VIZCore3DX.NET.Data.Vector3D(0.81781, 0.40693, -0.40693));

            VIZCore3DX.NET.Data.PolyhedralFace face = new VIZCore3DX.NET.Data.PolyhedralFace(contour);
            polyhedral.Face.Add(face);
        }

        {
            VIZCore3DX.NET.Data.PolyhedralContour contour = new VIZCore3DX.NET.Data.PolyhedralContour();
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33873.75403, -5386.82035, 2253.81806), new VIZCore3DX.NET.Data.Vector3D(-0.57505, 0.60553, -0.55014));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.11946, -5208.50230, 2075.50000), new VIZCore3DX.NET.Data.Vector3D(-0.57505, 0.60553, -0.55014));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34231.84897, -5216.70927, 2066.74943), new VIZCore3DX.NET.Data.Vector3D(-0.57505, 0.60553, -0.55014));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33873.48354, -5395.02732, 2245.06749), new VIZCore3DX.NET.Data.Vector3D(-0.57505, 0.60553, -0.55014));

            VIZCore3DX.NET.Data.PolyhedralFace face = new VIZCore3DX.NET.Data.PolyhedralFace(contour);
            polyhedral.Face.Add(face);
        }

        {
