            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33873.48354, -5395.02732, 2245.06749), new VIZCore3DX.NET.Data.Vector3D(-0.02254, -0.68391, -0.72921));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34231.84897, -5216.70927, 2066.74943), new VIZCore3DX.NET.Data.Vector3D(-0.02254, -0.68391, -0.72921));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.68297, -5217.56740, 2067.56741), new VIZCore3DX.NET.Data.Vector3D(0.00000, -0.70711, -0.70711));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33874.31755, -5395.88545, 2245.88546), new VIZCore3DX.NET.Data.Vector3D(0.00000, -0.70711, -0.70711));

            VIZCore3DX.NET.Data.PolyhedralFace face = new VIZCore3DX.NET.Data.PolyhedralFace(contour);
            polyhedral.Face.Add(face);
