            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33874.31755, -5395.88545, 2245.88546), new VIZCore3DX.NET.Data.Vector3D(0.57549, -0.57828, 0.57828));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.68297, -5217.56740, 2067.56741), new VIZCore3DX.NET.Data.Vector3D(0.57549, -0.57828, 0.57828));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.68297, -5209.08212, 2076.05269), new VIZCore3DX.NET.Data.Vector3D(0.57549, -0.57828, 0.57828));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33874.31755, -5387.40017, 2254.37074), new VIZCore3DX.NET.Data.Vector3D(0.57549, -0.57828, 0.57828));

            VIZCore3DX.NET.Data.PolyhedralFace face = new VIZCore3DX.NET.Data.PolyhedralFace(contour);
            polyhedral.Face.Add(face);
        }

        {
            VIZCore3DX.NET.Data.PolyhedralContour contour = new VIZCore3DX.NET.Data.PolyhedralContour();
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33874.31755, -5387.40017, 2254.37074), new VIZCore3DX.NET.Data.Vector3D(0.00000, 0.70711, 0.70711));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.68297, -5209.08212, 2076.05269), new VIZCore3DX.NET.Data.Vector3D(0.00000, 0.70711, 0.70711));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(34232.11946, -5208.50230, 2075.50000), new VIZCore3DX.NET.Data.Vector3D(0.02254, 0.68391, 0.72921));
            contour.Add(new VIZCore3DX.NET.Data.Vertex3D(33873.75403, -5386.82035, 2253.81806), new VIZCore3DX.NET.Data.Vector3D(0.02254, 0.68391, 0.72921));

            VIZCore3DX.NET.Data.PolyhedralFace face = new VIZCore3DX.NET.Data.PolyhedralFace(contour);
            polyhedral.Face.Add(face);
        }

        vizcore3dx.MeshEdit.CreateBodyPrimitivePolyhedral(
            partNode.Index      /* Node Index */
            , polyhedral        /* Polyhedral Model */
            , false             /* Rebuild Data */
            );
    }

    vizcore3dx.Structure.RebuildData();
