private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // New Empty Model
    int index = vizcore3dx.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3DX.NET.Data.Node assemblyNode =
        vizcore3dx.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3DX.NET.Data.Node partNode =
        vizcore3dx.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3DX.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3dx.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3dx.Object3D.GetBodyIndex(bodyId);

    {
        VIZCore3DX.NET.Data.PolyhedralModel polyhedral = new VIZCore3DX.NET.Data.PolyhedralModel();
        polyhedral.ModelColor = Color.Green;

        {
