    foreach (KeyValuePair<int, string> item in data)
    {
        int id = item.Key;
        string name = item.Value;

        // Get Metadata Binary
        byte[] buffer = vizcore3dx.Metadata.GetData(id);

        // Some code here...
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    // Delete Items
    int[] items = new int[] { 4, 5, 6, 10};

    foreach (int item in items)
    {
        if (vizcore3dx.Metadata.Exist(item) == false) continue;

        vizcore3dx.Metadata.Delete(item);
    }
}


