// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    int id = 43;

    string name_old = "BOM.xls";
    string name_new = "BLOCK_DIVISION.xls";

    if (vizcore3dx.Metadata.Exist(id) == false) return;

    string name = vizcore3dx.Metadata.GetName(id);

    if (name != name_old) return;

    bool result = vizcore3dx.Metadata.SetName(id, name_new);
}


