// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void ProgressChangedEvent()
{
    vizcore3dx.Model.OnModelProgressChangedEvent += Model_OnModelProgressChangedEvent;

    string path = "C:\\Model\\BLOCK.viz";

    vizcore3dx.Model.Open(path);
}

private void Model_OnModelProgressChangedEvent(object sender, VIZCore3DX.NET.Event.EventManager.ModelProgressEventArgs e)
{
    string Mode = String.Empty;

    switch (e.Mode)
    {
        case VIZCore3DX.NET.Data.ModelProgressModes.READ:
            Mode = "파일 읽기 (열기)...";
            break;
        case VIZCore3DX.NET.Data.ModelProgressModes.WRITE:
            Mode = "파일 저장 ...";
            break;
        case VIZCore3DX.NET.Data.ModelProgressModes.MERGE:
            Mode = "파일 추가 (병합)...";
            break;
        default:
            break;
    }

    System.Diagnostics.Trace.WriteLine(string.Format("{0} - Progress : {1} %", Mode, e.Progress));
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void AddStreamFile(Dictionary<string, byte[]> source)
{
    vizcore3dx.Model.OnStreamProgressChangedEvent += Model_OnStreamProgressChangedEvent;

    List<VIZCore3DX.NET.Data.StreamData> stream = new List<VIZCore3DX.NET.Data.StreamData>();

    foreach (KeyValuePair<string, byte[]> item in source)
    {
        VIZCore3DX.NET.Data.StreamData data = new VIZCore3DX.NET.Data.StreamData(
            item.Value  /* byte[] */
            , item.Key  /* Model Name */
            );

        stream.Add(data);
    }

    vizcore3dx.Model.AddStream(stream);
}

private void Model_OnStreamProgressChangedEvent(object sender, VIZCore3DX.NET.Event.EventManager.StreamProgressEventArgs e)
{
    System.Diagnostics.Trace.WriteLine(string.Format("[{0}/{1}] {2} : {3}", e.Index, e.Count, e.ModelName, e.Result));
}


