// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    OpenFileDialog dlgInput = new OpenFileDialog();
    dlgInput.Multiselect = true;
    dlgInput.Filter = "VIZ (*.viz)|*.viz";
    if (dlgInput.ShowDialog() != DialogResult.OK) return;

    string[] files = dlgInput.FileNames;

    string output = String.Empty;
    SaveFileDialog dlgOutput = new SaveFileDialog();
    dlgOutput.Filter = "VIZ (*.viz)|*.viz";
    if (dlgOutput.ShowDialog() != DialogResult.OK) return;

    output = dlgOutput.FileName;

    vizcore3dx.Model.MergeToVIZ(
        files.ToList()
        , output
        , Manager.ModelManager.FileVersion.V303
        , true
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    string input = "C:\\Model\\Input.vizx";
    string output = "C:\\Model\\Output.vizx";

    vizcore3dx.Model.ExportSimplifiedModel(
        input                                                                   /* INPUT FILE */
        , output                                                                /* OUTPUT FILE */
        , false                                                                 /* KEEP Structure */
        , false                                                                 /* Limit BoundBox */
        , 0.0f                                                                  /* BoundBox - Min. X */
        , 0.0f                                                                  /* BoundBox - Min. Y */
        , 0.0f                                                                  /* BoundBox - Min. Z */
        , 0.0f                                                                  /* BoundBox - Max. X */
        , 0.0f                                                                  /* BoundBox - Max. Y */
        , 0.0f                                                                  /* BoundBox - Max. Z */
        , false                                                                 /* Limit Mesh Count */
        , 0                                                                     /* Mesh Count */
        , VIZCore3DX.NET.Manager.ModelManager.SimplifiedUnit.TRIANGLE_MESH       /* Unit */
        , 1024                                                                  /* Virtual Screen Size */
        , 1                                                                     /* Virtual Screen Count */
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    List<string> keyword = new List<string>();
    keyword.Add("(OBST)");
    keyword.Add("(INSU)");

    List<VIZCore3DX.NET.Data.Node> nodes =
        vizcore3dx.Object3D.Find.QuickSearch(
            keyword /* Keyword */
            , false /* Join Codition : True(AND), False(OR) */
            , false /* Assembly Only : True(Assembly Only), False(All) */
            , false /* Visible Only : True(Visible Only), False(All) */
            , false /* Selected Only : True(Selected Only), False(All) */
            , false /* Full Keyword Match : True(Full Match), False(Contains) */
            , false /* Contain NodePath : True(Contain), False(Except) */
            );

    if (nodes.Count == 0) return;

    vizcore3dx.Object3D.Show(nodes, false);

    string output = "C:\\Temp\\Output.vizx";

    vizcore3dx.Model.ExportSimplifiedModel(
        output      /* OUTPUT */
        , false     /* Keep Structure */
        , VIZCore3DX.NET.Manager.ModelManager.SimplifiedUnit.TRIANGLE_SET_MESH
        , 1024      /* Virtual Screen Size */
        , 1         /* Virtual Screen Count */
        , VIZCore3DX.NET.Manager.ModelManager.ExcludeHiddenObjects.EXPORT_VISIBLE
        , true
        );
}


// VIZCore3DX.NET Control
