// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void StructureFromFile()
{
    string path = "C:\\Model\\BLOCK.vizx";

    VIZCore3DX.NET.ShdCore.StructureManager stru = 
        VIZCore3DX.NET.Manager.ModelManager.GetModelStructure(path);

    List<VIZCore3DX.NET.ShdCore.ModelTreeNode> roots = stru.Roots;

    // Structure
    foreach (VIZCore3DX.NET.ShdCore.ModelTreeNode root in roots)
    {
        string name = root.NodeName;

        if(root.Nodes.Count != 0)
        {
            NavigateNodes(root);
        }
    }

    // Node List
    foreach (VIZCore3DX.NET.ShdCore.ModelTreeNode item in stru.GetStructureNodeList())
    {
        // Some code here...
    }
}

private void NavigateNodes(VIZCore3DX.NET.ShdCore.ModelTreeNode parent)
{
    foreach (VIZCore3DX.NET.ShdCore.ModelTreeNode item in parent.Nodes)
    {
        string name = item.NodeName;

        if(item.Nodes.Count != 0)
        {
            NavigateNodes(item);
        }
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // INPUT
    OpenFileDialog dlg = new OpenFileDialog();
    dlg.Filter = "VIZX File (*.vizx)|*.vizx";
    if (dlg.ShowDialog() != DialogResult.OK) return;

    // OUTPUT DIR.
    FolderBrowserDialog output = new FolderBrowserDialog();
    output.SelectedPath = System.IO.Path.GetDirectoryName(dlg.FileName);
    if (output.ShowDialog() != DialogResult.OK) return;

    List<int> id = new List<int>();

    // Load Structure
    VIZCore3DX.NET.ShdCore.StructureManager stru =
        new VIZCore3DX.NET.ShdCore.StructureManager(dlg.FileName);

    // All Node
    List<VIZCore3DX.NET.ShdCore.ModelTreeNode> nodes = stru.GetStructureNodeList();

    // Find Node
    foreach (VIZCore3DX.NET.ShdCore.ModelTreeNode node in nodes)
    {
        if (node.NodeName.Contains("PIPE") == false) continue;
        id.Add(Convert.ToInt32(node.EntityId));
    }

    if (id.Count == 0) return;

    // Export
    vizcore3dx.Model.ExportNodes(
        dlg.FileName            /* INPUT (VIZX) */
        , output.SelectedPath   /* OUTPUT DIR. */
        , id                    /* NODE ID */
        );
}


