private void MouseBasedObjectMove_OnMouseUpEvent(object sender, VIZCore3DX.NET.Event.EventManager.MouseBaseObjectMoveEventArgs e)
{
    string str = string.Format("Mouse Up : {0} / {1} / {2} / {3} / {4} / {5} / {6}"
        , e.MoveOption.ToString()
        , e.GroupId
        , e.Move.ToString()
        , e.Anlge
        , e.MousePosition.X
        , e.MousePosition.Y
        , e.Node.Count()
        );

    System.Diagnostics.Debug.WriteLine(str);

    string ptStr = string.Format("X: {0}=={1} / Y: {2}=={3}", MousePt.X, e.MousePosition.X, MousePt.Y, e.MousePosition.Y);
    System.Diagnostics.Debug.WriteLine(ptStr);

    List<VIZCore3DX.NET.Data.NearestObjectByAxisPoint> items 
        = vizcore3dx.GeometryUtility.GetNearestObjects(
            e.MousePosition.X       /* Mouse Location X */
            , e.MousePosition.Y     /* Mouse Location Y */
            , true                  /* Visible Only */
            , false                 /* Opaque Only */
            );

    if (items.Count != 1) return;

    // 현재 이동되는 개체의 연계된 리뷰 정보(아이디) 조회
    List<int> review = vizcore3dx.View.MouseBasedObjectMove.GetLinkedReview(e.GroupId);
    if (review.Count == 0) return;

    // 해당 리뷰(노트)의 정보 조회
    VIZCore3DX.NET.Data.NoteItem note = vizcore3dx.Review.Note.GetItem(review[0]);

    // 해당 리뷰(노트)의 위치 정보 조회
    List<VIZCore3DX.NET.Data.ReviewPosition> position = note.Position;

    // 마우스가 이동한 거리만큼 리뷰의 위치를 재설정
    note.UpdatePosition(position[0], position[0].Position - new VIZCore3DX.NET.Data.Vertex3D(BlockMovingDistance.X, BlockMovingDistance.Y, BlockMovingDistance.Z));

    // 원래 이동전 위치로 이동
    vizcore3dx.Object3D.Transform.Move(e.Node.ToArray(), -BlockMovingDistance.X, -BlockMovingDistance.Y, -BlockMovingDistance.Z, false);
}

private void MouseBasedObjectMove_OnMouseDoubleClickEvent(object sender, Event.EventManager.MouseBaseObjectMoveEventArgs e)
{
    string str = string.Format("Mouse DoubleClick : {0} / {1} / {2} / {3} / {4} / {5} / {6}"
        , e.MoveOption.ToString()
        , e.GroupId
        , e.Move.ToString()
        , e.Anlge
        , e.MousePosition.X
        , e.MousePosition.Y
        , e.Node.Count()
        );

    System.Diagnostics.Debug.WriteLine(str);

    List<VIZCore3DX.NET.Data.NearestObjectByAxisPoint> items
        = vizcore3dx.GeometryUtility.GetNearestObjects(
            e.MousePosition.X       /* Mouse Location X */
            , e.MousePosition.Y     /* Mouse Location Y */
            , true                  /* Visible Only */
            , false                 /* Opaque Only */
            );

    if (items.Count == 0) return;

    // 선택된 블록 90도 회전
    vizcore3dx.Object3D.Transform.Rotate(e.Node.ToArray(), 0, 0, 90, false, false);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

// 마우스 위치
public System.Drawing.Point MousePt { get; set; }

// 선택 BLOCK 이동 거리
public VIZCore3DX.NET.Data.Vertex3D BlockMovingDistance { get; set; }

private void Example()
{
    // 마우스 다운 이벤트
    vizcore3dx.View.MouseBasedObjectMove.OnMouseDownEvent += MouseBasedObjectMove_OnMouseDownEvent;

    // 마우스 이동 이벤트
    vizcore3dx.View.MouseBasedObjectMove.OnMouseMoveEvent += MouseBasedObjectMove_OnMouseMoveEvent;

    // 마우스 업 이벤트
    vizcore3dx.View.MouseBasedObjectMove.OnMouseUpEvent += MouseBasedObjectMove_OnMouseUpEvent;

    // 마우스 더블클릭 이벤트
    vizcore3dx.View.MouseBasedObjectMove.OnMouseDoubleClickEvent += MouseBasedObjectMove_OnMouseDoubleClickEvent;
}

private void MouseBasedObjectMove_OnMouseDownEvent(object sender, VIZCore3DX.NET.Event.EventManager.MouseBaseObjectMoveEventArgs e)
{
    string str = string.Format("Mouse Down : {0} / {1} / {2} / {3} / {4} / {5} / {6}"
        , e.MoveOption.ToString()
        , e.GroupId
        , e.Move.ToString()
        , e.Anlge
        , e.MousePosition.X
        , e.MousePosition.Y
        , e.Node.Count()
        );

    System.Diagnostics.Debug.WriteLine(str);

    // Mouse Down 위치 백업
    MousePt = e.MousePosition;

    // 선택 BLOCK 위치
    BlockMovingDistance = new VIZCore3DX.NET.Data.Vertex3D(0, 0, 0);
}

private void MouseBasedObjectMove_OnMouseMoveEvent(object sender, VIZCore3DX.NET.Event.EventManager.MouseBaseObjectMoveEventArgs e)
{
    string str = string.Format("Mouse Move : {0} / {1} / {2} / {3} / {4} / {5} / {6}"
        , e.MoveOption.ToString()
        , e.GroupId
        , e.Move.ToString()
        , e.Anlge
        , e.MousePosition.X
        , e.MousePosition.Y
        , e.Node.Count()
        );

    System.Diagnostics.Debug.WriteLine(str);

    // 마우스로 회전일 경우는 중단
    if (e.MoveOption == VIZCore3DX.NET.Manager.MouseBasedObjectMoveManager.MovingOptions.ROTATION) return;

    // 현재 이동되는 개체의 연계된 리뷰 정보(아이디) 조회
    List<int> review = vizcore3dx.View.MouseBasedObjectMove.GetLinkedReview(e.GroupId);
    if (review.Count == 0) return;

    // 해당 리뷰(노트)의 정보 조회
    VIZCore3DX.NET.Data.NoteItem note = vizcore3dx.Review.Note.GetItem(review[0]);

    // 해당 리뷰(노트)의 위치 정보 조회
    List<VIZCore3DX.NET.Data.ReviewPosition> position = note.Position;

    // 마우스가 이동한 거리만큼 리뷰의 위치를 재설정
    note.UpdatePosition(position[0], position[0].Position + e.Move);

    // BLOCK 이동 거리 누적
    BlockMovingDistance = BlockMovingDistance + e.Move;
}

private void MouseBasedObjectMove_OnMouseUpEvent(object sender, VIZCore3DX.NET.Event.EventManager.MouseBaseObjectMoveEventArgs e)
{
    string str = string.Format("Mouse Up : {0} / {1} / {2} / {3} / {4} / {5} / {6}"
        , e.MoveOption.ToString()
        , e.GroupId
        , e.Move.ToString()
        , e.Anlge
        , e.MousePosition.X
        , e.MousePosition.Y
        , e.Node.Count()
        );

    System.Diagnostics.Debug.WriteLine(str);

    string ptStr = string.Format("X: {0}=={1} / Y: {2}=={3}", MousePt.X, e.MousePosition.X, MousePt.Y, e.MousePosition.Y);
    System.Diagnostics.Debug.WriteLine(ptStr);

    List<VIZCore3DX.NET.Data.NearestObjectByAxisPoint> items 
        = vizcore3dx.GeometryUtility.GetNearestObjects(
            e.MousePosition.X       /* Mouse Location X */
            , e.MousePosition.Y     /* Mouse Location Y */
            , true                  /* Visible Only */
            , false                 /* Opaque Only */
            );

    if (items.Count != 1) return;

    // 현재 이동되는 개체의 연계된 리뷰 정보(아이디) 조회
    List<int> review = vizcore3dx.View.MouseBasedObjectMove.GetLinkedReview(e.GroupId);
    if (review.Count == 0) return;

    // 해당 리뷰(노트)의 정보 조회
    VIZCore3DX.NET.Data.NoteItem note = vizcore3dx.Review.Note.GetItem(review[0]);

    // 해당 리뷰(노트)의 위치 정보 조회
    List<VIZCore3DX.NET.Data.ReviewPosition> position = note.Position;

    // 마우스가 이동한 거리만큼 리뷰의 위치를 재설정
    note.UpdatePosition(position[0], position[0].Position - new VIZCore3DX.NET.Data.Vertex3D(BlockMovingDistance.X, BlockMovingDistance.Y, BlockMovingDistance.Z));

    // 원래 이동전 위치로 이동
    vizcore3dx.Object3D.Transform.Move(e.Node.ToArray(), -BlockMovingDistance.X, -BlockMovingDistance.Y, -BlockMovingDistance.Z, false);
}

private void MouseBasedObjectMove_OnMouseDoubleClickEvent(object sender, Event.EventManager.MouseBaseObjectMoveEventArgs e)
{
    string str = string.Format("Mouse DoubleClick : {0} / {1} / {2} / {3} / {4} / {5} / {6}"
        , e.MoveOption.ToString()
        , e.GroupId
        , e.Move.ToString()
        , e.Anlge
        , e.MousePosition.X
        , e.MousePosition.Y
        , e.Node.Count()
        );

    System.Diagnostics.Debug.WriteLine(str);

    List<VIZCore3DX.NET.Data.NearestObjectByAxisPoint> items
        = vizcore3dx.GeometryUtility.GetNearestObjects(
            e.MousePosition.X       /* Mouse Location X */
            , e.MousePosition.Y     /* Mouse Location Y */
            , true                  /* Visible Only */
            , false                 /* Opaque Only */
            );

    if (items.Count == 0) return;

    // 선택된 블록 90도 회전
    vizcore3dx.Object3D.Transform.Rotate(e.Node.ToArray(), 0, 0, 90, false, false);
}


