// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    // 기본 데이터 생성
    VIZCore3DX.NET.Data.SurfaceNoteDefaultData item = new VIZCore3DX.NET.Data.SurfaceNoteDefaultData();
    item.UseSymbol = true;
    item.SymbolText = "1";
    item.ModelNameLevel = 1;

    // 기본 데이터 설정
    vizcore3dx.Review.Note.SurfaceNoteDefaultValue = item;

    // 리뷰 변경 : 표면노트 추가 이벤트
    vizcore3dx.Review.OnReviewChangedEvent += Review_OnReviewChangedEvent;

    // 표면노트 추가
    vizcore3dx.Review.Note.AddNoteSurfaceDialog();
}

private void Review_OnReviewChangedEvent(object sender, Event.EventManager.ReviewEventArgs e)
{
    if (e.EventKind != Manager.ReviewManager.ReviewEventKind.ADDED_SURFACE_NOTE) return;

    VIZCore3DX.NET.Data.SurfaceNoteDefaultData item = vizcore3dx.Review.Note.SurfaceNoteDefaultValue;

    item.SymbolText = "2";
}


