// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Get Style        
    VIZCore3DX.NET.Data.NoteStyle noteStyle = vizcore3dx.Note.GetStyle();

    noteStyle.BoxFillColor = Color.Green;
    noteStyle.BoxStrokeColor = Color.Red;
    noteStyle.HighlightedBoxFillColor = Color.Blue;
    noteStyle.HighlightedBoxStrokeColor = Color.Purple;
    noteStyle.SelectedBoxStrokeColor = Color.Gray;
    noteStyle.TextColor = Color.White;
    noteStyle.TextHorizontalAlignment = Data.HorizontalAlignment.Left;
    noteStyle.TextSize = TextSizeType.Size_24;
    noteStyle.LineStrokeColor = Color.White;
    noteStyle.LineStrokePattern = StrokePattern.Dotted;
    noteStyle.LineStrokeThickness = 2;
    noteStyle.ArrowColor = Color.Red;
    noteStyle.ArrowHeadSize = 10;

    // Symbol
    noteStyle.SymbolFillColor = Color.Green;
    noteStyle.SymbolStrokeColor = Color.Red;
    noteStyle.SymbolSize = 24;
    noteStyle.SymbolTextColor = Color.White;
    noteStyle.SymbolTextSize = TextSizeType.Size_24;

    // Set Style
    vizcore3dx.Note.SetStyle(noteStyle);
}


