// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Review.Note.Items.Count == 0) return;

    // 겹쳐진 노트 항목 검색
    bool result = vizcore3dx.Review.Note.FindOverlappingItems(true);

    // 겹쳐진 노트 항목이 없음
    if (result == false)
    {
        foreach (VIZCore3DX.NET.Data.NoteItem item in vizcore3dx.Review.Note.Items)
        {
            VIZCore3DX.NET.Data.NoteStyle style = vizcore3dx.Review.Note.GetStyle(item.ID);
            style.BackgroundColor = Color.Yellow;
            vizcore3dx.Review.Note.SetStyle(item.ID, style);
        }

        return;
    }

    vizcore3dx.BeginUpdate();

    foreach (VIZCore3DX.NET.Data.NoteItem item in vizcore3dx.Review.Note.Items)
    {
        VIZCore3DX.NET.Data.NoteStyle style = vizcore3dx.Review.Note.GetStyle(item.ID);

        // 현재 노트와 겹쳐진 노트가 없음
        if (item.OverlappingItems.Count == 0)
        {
            style.BackgroundColor = Color.Yellow;
        }
        // 현재 노트와 겹쳐진 노트가 있음
        else
        {
            // 겹쳐진 노트의 아이디가 현재보다 클 경우, 
            // 나중에 생성된 노트이고,
            // 현재 노트보다 위에 그려짐.
            if (item.ID < item.OverlappingItems[0])
            {
                style.BackgroundColor = Color.Red;
            }
            else
            {
                style.BackgroundColor = Color.White;
            }
        }

        vizcore3dx.Review.Note.SetStyle(item.ID, style);
    }

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Review.Note.Items.Count == 0) return;

    List<int> excludedNote = new List<int>();
    excludedNote.Add(4);
    excludedNote.Add(5);

    // 겹쳐진 노트 항목 검색
    bool result = vizcore3dx.Review.Note.FindOverlappingItems(true, null, excludedNote);

    // 겹쳐진 노트 항목이 없음
    if (result == false)
    {
        foreach (VIZCore3DX.NET.Data.NoteItem item in vizcore3dx.Review.Note.Items)
        {
            VIZCore3DX.NET.Data.NoteStyle style = vizcore3dx.Review.Note.GetStyle(item.ID);
            style.BackgroundColor = Color.Yellow;
            vizcore3dx.Review.Note.SetStyle(item.ID, style);
        }

        return;
    }

    vizcore3dx.BeginUpdate();

    foreach (VIZCore3DX.NET.Data.NoteItem item in vizcore3dx.Review.Note.Items)
    {
        VIZCore3DX.NET.Data.NoteStyle style = vizcore3dx.Review.Note.GetStyle(item.ID);

        // 현재 노트와 겹쳐진 노트가 없음
        if (item.OverlappingItems.Count == 0)
        {
            style.BackgroundColor = Color.Yellow;
        }
        // 현재 노트와 겹쳐진 노트가 있음
        else
        {
            // 겹쳐진 노트의 아이디가 현재보다 클 경우, 
            // 나중에 생성된 노트이고,
            // 현재 노트보다 위에 그려짐.
            if (item.ID < item.OverlappingItems[0])
            {
                style.BackgroundColor = Color.Red;
            }
            else
            {
                style.BackgroundColor = Color.White;
            }
        }

        vizcore3dx.Review.Note.SetStyle(item.ID, style);
    }

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Object3D.OnObject3DSelected += Object3D_OnObject3DVisibleChangedEvent;
}

private void Object3D_OnObject3DVisibleChangedEvent(object sender, Event.EventManager.Object3DVisibleChangedEventArgs e)
{
    if (e.Node.Count == 0) return;

    bool bTemp = e.Visible; //false: 숨기기 / true: 보이기
    List<Data.Node> nodes = e.Node;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Object3D.OnObject3DSelected += Object3D_OnNodeEvent;
}

private void Object3D_OnNodeEvent(object sender, Event.EventManager.NodeEventArgs e)
{
    if (e.Node.Count == 0) return;

    Dictionary<string, string> dic = vizcore3dx.Object3D.UDA.FromNode(e.Node[0], false).ToDictionary();

    if (e.EventKind == Object3DManager.NodeEventKind.DELETED_NDOE)
    {
        List<Data.Node> nodes = e.Node;
    }
}


