// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Object3D.OnObject3DSelected += Object3D_OnObject3DSelected;
}

private void Object3D_OnObject3DSelected(object sender, Event.EventManager.Object3DSelectedEventArgs e)
{
    if (e.Node.Count == 0) return;

    List<int> orderedIndex = vizcore3dx.Object3D.GetSelectedIndexInSelectedOrder();
    List<VIZCore3DX.NET.Data.Node> orderedNode = vizcore3dx.Object3D.GetSelectedNodeInSelectedOrder();

    for (int i = 0; i < orderedIndex.Count; i++)
    {
        System.Diagnostics.Debug.WriteLine(orderedIndex[i]);

        // Or
        System.Diagnostics.Debug.WriteLine(orderedNode[i].NodeName);
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Object3D.OnObject3DSelected += Object3D_OnObject3DSelected;
}

private void Object3D_OnObject3DSelected(object sender, Event.EventManager.Object3DSelectedEventArgs e)
{
    if (e.Node.Count == 0) return;

    List<int> orderedIndex = vizcore3dx.Object3D.GetSelectedIndexInSelectedOrder();
    List<VIZCore3DX.NET.Data.Node> orderedNode = vizcore3dx.Object3D.GetSelectedNodeInSelectedOrder();

    for (int i = 0; i < orderedIndex.Count; i++)
    {
        System.Diagnostics.Debug.WriteLine(orderedIndex[i]);

        // Or
        System.Diagnostics.Debug.WriteLine(orderedNode[i].NodeName);
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example(Data.Node node)
{
    // 지정된 노드 바로 하위 노드 목록
    List<VIZCore3DX.NET.Data.Node> children =
        vizcore3dx.Object3D.GetChildObject3d(
            node
            , VIZCore3DX.NET.Data.Object3DChildOption.CHILD_ONLY
            );

    // 지정된 노드 하위 전체 목록
    List<VIZCore3DX.NET.Data.Node> all_children =
        vizcore3dx.Object3D.GetChildObject3d(
            node
            , VIZCore3DX.NET.Data.Object3DChildOption.ALL_CHILDREN
            );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    float x1, y1, z1, x2, y2, z2;
    x1 = 0.0f;
    y1 = 0.0f;
    z1 = 0.0f;

    x2 = 100.0f;
    y2 = 100.0f;
    z2 = 100.0f;

    List<VIZCore3DX.NET.Data.Node> items =
        vizcore3dx.Object3D.FromZone(
            x1, y1, z1
            , x2, y2, z2
            , VIZCore3DX.NET.Data.BoundBoxSearchOption.FullyContained
            );
}

private void Example2()
{
    VIZCore3DX.NET.Data.Vertex3D min = new Data.Vertex3D(0, 0, 0);
    VIZCore3DX.NET.Data.Vertex3D max = new Data.Vertex3D(100, 100, 100);

    List<VIZCore3DX.NET.Data.Node> items =
        vizcore3dx.Object3D.FromZone(
            min
            , max
            , VIZCore3DX.NET.Data.BoundBoxSearchOption.FullyContained
            );
}

