// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int nodeIndex = 8;

    List<VIZCore3DX.NET.Data.Object3DEdgeVertex> items =
        vizcore3dx.Object3D.GetEdgeVertex(nodeIndex, true);

    foreach (VIZCore3DX.NET.Data.Object3DEdgeVertex item in items)
    {
        int index = item.Index;
        List<VIZCore3DX.NET.Data.Vertex3D> vItems = item.Edge;

        foreach (VIZCore3DX.NET.Data.Vertex3D point in vItems)
        {
            float x = point.X;
            float y = point.Y;
            float z = point.Z;
        }
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    Dictionary<int, int> items = vizcore3dx.Object3D.CopyNodes();

    if (items.Count == 0)
    {
        MessageBox.Show("Node is null.", "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Warning);
        return;
    }

    foreach (KeyValuePair<int, int> item in items)
    {
        VIZCore3DX.NET.Data.Node source = vizcore3dx.Object3D.FromIndex(item.Key);
        VIZCore3DX.NET.Data.Node target = vizcore3dx.Object3D.FromIndex(item.Value);

        MessageBox.Show(
            string.Format(
                "{0} - {1}, {2} / {3}"
            , item.Key
            , item.Value
            , source.NodeName
            , target.NodeName
            )
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<VIZCore3DX.NET.Data.Node> selectedItems =
        vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_PART);

    Dictionary<int, int> items = vizcore3dx.Object3D.CopyNodes(selectedItems);

    if (items.Count == 0)
    {
        MessageBox.Show("Node is null.", "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Warning);
        return;
    }

    foreach (KeyValuePair<int, int> item in items)
    {
        VIZCore3DX.NET.Data.Node source = vizcore3dx.Object3D.FromIndex(item.Key);
        VIZCore3DX.NET.Data.Node target = vizcore3dx.Object3D.FromIndex(item.Value);

        MessageBox.Show(
            string.Format(
                "{0} - {1}, {2} / {3}"
            , item.Key
            , item.Value
            , source.NodeName
            , target.NodeName
            )
            , "VIZCore3DX.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int parentIndex = 4;
    int childIndex = 100;

    bool result = vizcore3dx.Object3D.IsChildObject3d(parentIndex, childIndex);

    if (result == true)
        System.Console.WriteLine("Index is a child.");
    else
        System.Console.WriteLine("Index is not a child.");
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // 어셈블리 노드 목록
    List<VIZCore3DX.NET.Data.Node> items = vizcore3dx.Object3D.GetPartialNode(true, false, false);

    List<VIZCore3DX.NET.Data.Node> result = new List<VIZCore3DX.NET.Data.Node>();

    // 필터링
    foreach (VIZCore3DX.NET.Data.Node item in items)
    {
        string[] vals = item.SplitNodeName("/");
        if (vals.Length != 4) continue;

        result.Add(item);
    }

    // 노드의 부가정보 채우기 : Index 및 Node Name만 필요한 경우, 호출 불필요.
    result = vizcore3dx.Object3D.FillNodeData(result);
}

private void CheckPerformance()
{
    System.Text.StringBuilder sb = new System.Text.StringBuilder();

    sb.AppendLine(string.Format("NODE COUNT : {0:#,0} EA", vizcore3dx.Object3D.GetNodeCount()));
    sb.AppendLine();

    System.Diagnostics.Stopwatch sw = new System.Diagnostics.Stopwatch();

    sw.Start();
    List<VIZCore3DX.NET.Data.Node> allSync = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.ALL);
    sw.Stop();
    sb.AppendLine(string.Format("SYNC. ALL : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allAssemblySync = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.ASSEMBLY);
    sw.Stop();
    sb.AppendLine(string.Format("SYNC. ASSEMBLY : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allPartSync = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.PART);
    sw.Stop();
    sb.AppendLine(string.Format("SYNC. PART : {0:#,0} milliseconds", sw.ElapsedMilliseconds));
    sb.AppendLine();

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allAsync = vizcore3dx.Object3D.GetPartialNode(true, true, true);
    sw.Stop();
    sb.AppendLine(string.Format("ASYNC. ALL : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allAssyPartAsync = vizcore3dx.Object3D.GetPartialNode(true, true, false);
    sw.Stop();
    sb.AppendLine(string.Format("ASYNC. ASSEMBLY + PART : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allAssemblyAsync = vizcore3dx.Object3D.GetPartialNode(true, false, false);
    sw.Stop();
    sb.AppendLine(string.Format("ASYNC. ASSEMBLY : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allPartAsync = vizcore3dx.Object3D.GetPartialNode(false, true, false);
    sw.Stop();
    sb.AppendLine(string.Format("ASYNC. PART : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    sw.Restart();
    List<VIZCore3DX.NET.Data.Node> allBodyAsync = vizcore3dx.Object3D.GetPartialNode(false, false, true);
    sw.Stop();
    sb.AppendLine(string.Format("ASYNC. BODY : {0:#,0} milliseconds", sw.ElapsedMilliseconds));

    MessageBox.Show(sb.ToString());
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // 어셈블리 노드 목록
    List<VIZCore3DX.NET.Data.Node> items = vizcore3dx.Object3D.GetPartialNode(true, false, false);

    List<VIZCore3DX.NET.Data.Node> result = new List<VIZCore3DX.NET.Data.Node>();

    // 필터링
    foreach (VIZCore3DX.NET.Data.Node item in items)
    {
        string[] vals = item.SplitNodeName("/");
        if (vals.Length != 4) continue;

        result.Add(item);
    }

    // 노드의 부가정보 채우기 : Index 및 Node Name만 필요한 경우, 호출 불필요.
    result = vizcore3dx.Object3D.FillNodeData(result);
}


