// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    VIZCore3DX.NET.Data.Node selectedNode 
        = vizcore3dx.Object3D.ShowAssemblyBrowserDialog();

    if (selectedNode == null) return;

    MessageBox.Show(selectedNode.NodeName);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<VIZCore3DX.NET.Data.Node> currentSelectedNode =
        vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP, false);

    VIZCore3DX.NET.Data.Node selectedNode 
        = vizcore3dx.Object3D.ShowAssemblyBrowserDialog(
            currentSelectedNode.Count != 0 ? currentSelectedNode[0] : null);

    if (selectedNode == null) return;

    MessageBox.Show(selectedNode.NodeName);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    // Hide OBST - Keyword : .... (OBST) 
    vizcore3dx.Object3D.Show("(OBST)", false, false);

    // Hide INSU - Keyword : .... (INSU)
    vizcore3dx.Object3D.Show("(INSU)", false, false);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    // Hide OBST - Keyword : .... (OBST) 
    vizcore3dx.Object3D.LockObjects("(OBST)", false, true);

    // Hide INSU - Keyword : .... (INSU)
    vizcore3dx.Object3D.LockObjects("(INSU)", false, true);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
   if (vizcore3dx.Model.IsOpen() == false) return;

   if (vizcore3dx.Section.SelectedItem == null) return;

   VIZCore3DX.NET.Data.Section section = vizcore3dx.Section.SelectedItem;

   if (section.SectionType != VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION) return;

   List<VIZCore3DX.NET.Data.SectionPlane> items = section.Planes;

   if (items == null) return;

    vizcore3dx.Object3D.SplitObjects(
        nodes                   /* Selection Node List*/
        , sectionId             /* Section ID */
        , true                  /* SelectedFlagOnly */
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
   if (vizcore3dx.Model.IsOpen() == false) return;

   if (vizcore3dx.Section.SelectedItem == null) return;

   VIZCore3DX.NET.Data.Section section = vizcore3dx.Section.SelectedItem;

   if (section.SectionType != VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION) return;

   List<VIZCore3DX.NET.Data.SectionPlane> items = section.Planes;

   if (items == null) return;

    vizcore3dx.Object3D.SplitObjects(
        section.ID                /* Section ID */
        , false                   /* selectedOnly*/
        );
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
   if (vizcore3dx.Model.IsOpen() == false) return;

   if (vizcore3dx.Section.SelectedItem == null) return;

   VIZCore3DX.NET.Data.Section section = vizcore3dx.Section.SelectedItem;

   string path = "C:\\Model\\Outside.viz";

    vizcore3dx.Object3D.SplitObjects(
        path                    /* Save Path*/
        , sectionId             /* Section ID */
        );
}


