// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Color Index : rev2bst.config.rev 파일 참고

    VIZCore3DX.NET.Manager.PrimitiveObject root =
        vizcore3dx.Primitive.AddNode("Root", 4 /* Yellow Color*/);

    VIZCore3DX.NET.Manager.PrimitiveObject child1 =
        root.AddNode("CHILD1", 7 /* Blue Color */);

    {
        VIZCore3DX.NET.Manager.PrimitiveBox box1 = 
            new VIZCore3DX.NET.Manager.PrimitiveBox();

        box1.Set2Point(
            new float[] { 0, 0, 0 }
            , new float[] { 100, 100, 100 }
            , 50.0f
            );

        child1.AddPrimitive(box1);


        VIZCore3DX.NET.Manager.PrimitiveBox box2 =
            new VIZCore3DX.NET.Manager.PrimitiveBox();

        VIZCore3DX.NET.Data.BoundBox3D boundBox1 =
            new VIZCore3DX.NET.Data.BoundBox3D(
                new VIZCore3DX.NET.Data.Vertex3D(
                    200, 200, 200
                    )
                , new VIZCore3DX.NET.Data.Vertex3D(
                    300, 300, 300
                    )
                );

        box2.SetMinMaxPoints(boundBox1);

        child1.AddPrimitive(box2);
    }


    VIZCore3DX.NET.Manager.PrimitiveObject child2 =
        root.AddNode("CHILD2", 11 /* Pink Color */);

    {
        VIZCore3DX.NET.Manager.PrimitiveCylinder cylinder1 =
            new VIZCore3DX.NET.Manager.PrimitiveCylinder();

        cylinder1.Set2Point(
            new float[] { 400, 400, 400 }
            , new float[] { 600, 600, 600 }
            , 50.0f
            );

        child2.AddPrimitive(cylinder1);
    }

    // Open Model
    vizcore3dx.Primitive.OpenModel("Primitive", false);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Color Index : rev2bst.config.rev 파일 참고

    VIZCore3DX.NET.Manager.PrimitiveObject root =
        vizcore3dx.Primitive.AddNode("Root", 4 /* Yellow Color*/);

    VIZCore3DX.NET.Manager.PrimitiveObject child1 =
        root.AddNode("CHILD1", 7 /* Blue Color */);

    {
        VIZCore3DX.NET.Manager.PrimitiveBox box1 = 
            new VIZCore3DX.NET.Manager.PrimitiveBox();

        box1.Set2Point(
            new float[] { 0, 0, 0 }
            , new float[] { 100, 100, 100 }
            , 50.0f
            );

        child1.AddPrimitive(box1);


        VIZCore3DX.NET.Manager.PrimitiveBox box2 =
            new VIZCore3DX.NET.Manager.PrimitiveBox();

        VIZCore3DX.NET.Data.BoundBox3D boundBox1 =
            new VIZCore3DX.NET.Data.BoundBox3D(
                new VIZCore3DX.NET.Data.Vertex3D(
                    200, 200, 200
                    )
                , new VIZCore3DX.NET.Data.Vertex3D(
                    300, 300, 300
                    )
                );

        box2.SetMinMaxPoints(boundBox1);

        child1.AddPrimitive(box2);
    }


    VIZCore3DX.NET.Manager.PrimitiveObject child2 =
        root.AddNode("CHILD2", 11 /* Pink Color */);

    {
        VIZCore3DX.NET.Manager.PrimitiveCylinder cylinder1 =
            new VIZCore3DX.NET.Manager.PrimitiveCylinder();

        cylinder1.Set2Point(
            new float[] { 400, 400, 400 }
            , new float[] { 600, 600, 600 }
            , 50.0f
            );

        child2.AddPrimitive(cylinder1);
    }

    // Open Model
    vizcore3dx.Primitive.OpenModel("Primitive", false);
}


