// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Section.OnSectionEvent += Section_OnSectionEvent;
}

private void Section_OnSectionEvent(object sender, Event.EventManager.SectionEventArgs e)
{
    switch (e.EventType)
    {
        // 생성 이벤트
        case VIZCore3DX.NET.Manager.SectionManager.EventTypes.ADD:
            break;
        // 삭제 이벤트
        case VIZCore3DX.NET.Manager.SectionManager.EventTypes.DELETE:
            break;
        // 전체 삭제 이벤트
        case VIZCore3DX.NET.Manager.SectionManager.EventTypes.CLEAR:
            break;
        // 선택 이벤트
        case VIZCore3DX.NET.Manager.SectionManager.EventTypes.SELECT:
            break;
        // 이동 및 회전 이벤트
        case VIZCore3DX.NET.Manager.SectionManager.EventTypes.TRANSFORM:
            break;
        // 이동 완료
        case VIZCore3DX.NET.Manager.SectionManager.EventTypes.RESIZED:
            break;
        default:
            break;
    }

    // 단면 정보 없음
    if (e.Section == null)
        return;

    switch (e.Section.SectionType)
    {
        // 단면
        case VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION:
            break;
        // 상자
        case VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION_BOX:
            break;
        // 인박스
        case VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION_INBOX:
            break;
        default:
            break;
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Section.OnSectionBeforeAddEvent += Section_OnSectionBeforeAddEvent;
    vizcore3dx.Section.OnSectionAfterAddEvent += Section_OnSectionAfterAddEvent;
}

private void Section_OnSectionBeforeAddEvent(object sender, Event.EventManager.SectionBeforeAddEventArgs e)
{
    switch (e.Kind)
    {
        case Manager.SectionManager.SectionTypes.SECTION:
            {
                bool partSelection = e.PartSelection;
                VIZCore3DX.NET.Data.Axis axis = e.SectionAxis;
            }
            break;
        case Manager.SectionManager.SectionTypes.SECTION_BOX:
            {
                bool partSelection = e.PartSelection;
                float margine = e.Margine;
            }
            break;
        case Manager.SectionManager.SectionTypes.SECTION_INBOX:
            break;
        case Manager.SectionManager.SectionTypes.SECTION_INBOX6:
            break;
        default:
            break;
    }
}

private void Section_OnSectionAfterAddEvent(object sender, Event.EventManager.SectionAfterAddEventArgs e)
{
    VIZCore3DX.NET.Data.Section section = e.Item;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.Section.OnSectionBeforeAddEvent += Section_OnSectionBeforeAddEvent;
    vizcore3dx.Section.OnSectionAfterAddEvent += Section_OnSectionAfterAddEvent;
}

private void Section_OnSectionBeforeAddEvent(object sender, Event.EventManager.SectionBeforeAddEventArgs e)
{
    switch (e.Kind)
    {
        case Manager.SectionManager.SectionTypes.SECTION:
            {
                bool partSelection = e.PartSelection;
                VIZCore3DX.NET.Data.Axis axis = e.SectionAxis;
            }
            break;
        case Manager.SectionManager.SectionTypes.SECTION_BOX:
            {
                bool partSelection = e.PartSelection;
                float margine = e.Margine;
            }
            break;
        case Manager.SectionManager.SectionTypes.SECTION_INBOX:
            break;
        case Manager.SectionManager.SectionTypes.SECTION_INBOX6:
            break;
        default:
            break;
    }
}

private void Section_OnSectionAfterAddEvent(object sender, Event.EventManager.SectionAfterAddEventArgs e)
{
    VIZCore3DX.NET.Data.Section section = e.Item;
}


