// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example1()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    List<VIZCore3DX.NET.Data.Node> nodes
        = vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP);
    if (nodes.Count == 0) return;

    VIZCore3DX.NET.Data.BoundBox3D boundBox = vizcore3dx.Object3D.GetBoundBox(nodes);

    VIZCore3DX.NET.Data.SectionItem section = vizcore3dx.Section.AddBox(false);
    vizcore3dx.Section.SetBoxSize(section.ID, boundBox);

    vizcore3dx.View.FitToView(boundBox.GetCenter());
}

private void Example2()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    List<VIZCore3DX.NET.Data.Node> nodes
        = vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP);
    if (nodes.Count == 0) return;

    VIZCore3DX.NET.Data.SectionItem section = vizcore3dx.Section.AddBox(true);

    vizcore3dx.View.FitToView(section.BoundBox.GetCenter());
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    VIZCore3DX.NET.Data.Section section = vizcore3dx.Section.SelectedItem;
    if (section == null) return;

    // 1. Geometry Property
    VIZCore3DX.NET.Data.SectionGeometryProperty prop =
        vizcore3dx.Section.GetGeometryProperty();

    float area = prop.Area / 1000.0f / 1000.0f;
    string areaDisplayStr = string.Format("{0} ㎡", area);

    float volume = prop.Volume / 1000.0f / 1000.0f / 1000.0f;
    string volumeDisplayStr = string.Format("{0} ㎥", volume);

    // 2. Error Node Index
    foreach (int errorNodeIndex in prop.ErrorIndex)
    {
        Console.WriteLine(string.Format("Error Node Index : {0}", errorNodeIndex));
    }

    // 3. Clipped Parts
    List<VIZCore3DX.NET.Data.Node> nodes = new List<VIZCore3DX.NET.Data.Node>();

    if (section.SectionType == VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION)
    {
        nodes = vizcore3dx.Section.GetSectionNode(section.ID, -1);
    }
    else if (section.SectionType == VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION_BOX)
    {
        for (int i = 0; i < 6; i++)
        {
            nodes.AddRange(vizcore3dx.Section.GetSectionNode(section.ID, i));
        }
    }

    foreach (VIZCore3DX.NET.Data.Node node in nodes)
    {
        VIZCore3DX.NET.Data.SectionGeometryPartProperty partGeometry = null;

        if (prop.PartData.ContainsKey(node.Index) == true)
        {
            partGeometry = prop.PartData[node.Index];
        }
        else if (prop.PartData.ContainsKey(node.Index + 1) == true)
        {
            partGeometry = prop.PartData[node.Index + 1];
        }

        float orgArea = 0.0f;
        float orgVolume = 0.0f;
        float resultArea = 0.0f;
        float resultVolume = 0.0f;

        if (partGeometry != null)
        {
            orgArea = partGeometry.OriginalArea / 1000.0f / 1000.0f;
            orgVolume = partGeometry.OriginalVolume / 1000.0f / 1000.0f / 1000.0f;

            resultArea = partGeometry.ResultArea / 1000.0f / 1000.0f;
            resultVolume = partGeometry.ResultVolume / 1000.0f / 1000.0f / 1000.0f;
        }

        Console.WriteLine(
            string.Format(
                "{0}, {1}, {2}, {3}, {4}, {5}, {6}"
                , node.Index.ToString()
                , node.NodeName
                , node.GetParentName()
                , partGeometry == null ? "N/A" : orgArea.ToString()
                , partGeometry == null ? "N/A" : orgVolume.ToString()
                , partGeometry == null ? "N/A" : resultArea.ToString()
                , partGeometry == null ? "N/A" : resultVolume.ToString()
                )
            );
    }
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    VIZCore3DX.NET.Data.Section section = vizcore3dx.Section.SelectedItem;
    if (section == null) return;

    // 1. Geometry Property
    VIZCore3DX.NET.Data.SectionGeometryProperty prop =
        vizcore3dx.Section.GetGeometryProperty(true, false);

    float area = prop.Area / 1000.0f / 1000.0f;
    string areaDisplayStr = string.Format("{0} ㎡", area);

    float volume = prop.Volume / 1000.0f / 1000.0f / 1000.0f;
    string volumeDisplayStr = string.Format("{0} ㎥", volume);

    // 2. Error Node Index
    foreach (int errorNodeIndex in prop.ErrorIndex)
    {
        Console.WriteLine(string.Format("Error Node Index : {0}", errorNodeIndex));
    }

    // 3. Clipped Parts
    List<VIZCore3DX.NET.Data.Node> nodes = new List<VIZCore3DX.NET.Data.Node>();

    if (section.SectionType == VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION)
    {
        nodes = vizcore3dx.Section.GetSectionNode(section.ID, -1);
    }
    else if (section.SectionType == VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION_BOX)
    {
        for (int i = 0; i < 6; i++)
        {
            nodes.AddRange(vizcore3dx.Section.GetSectionNode(section.ID, i));
        }
    }

    foreach (VIZCore3DX.NET.Data.Node node in nodes)
    {
        VIZCore3DX.NET.Data.SectionGeometryPartProperty partGeometry = null;

        if (prop.PartData.ContainsKey(node.Index) == true)
        {
            partGeometry = prop.PartData[node.Index];
        }
        else if (prop.PartData.ContainsKey(node.Index + 1) == true)
        {
            partGeometry = prop.PartData[node.Index + 1];
        }

        float orgArea = 0.0f;
        float orgVolume = 0.0f;
        float resultArea = 0.0f;
        float resultVolume = 0.0f;

        if (partGeometry != null)
        {
            orgArea = partGeometry.OriginalArea / 1000.0f / 1000.0f;
            orgVolume = partGeometry.OriginalVolume / 1000.0f / 1000.0f / 1000.0f;

            resultArea = partGeometry.ResultArea / 1000.0f / 1000.0f;
            resultVolume = partGeometry.ResultVolume / 1000.0f / 1000.0f / 1000.0f;
        }

        Console.WriteLine(
            string.Format(
                "{0}, {1}, {2}, {3}, {4}, {5}, {6}"
                , node.Index.ToString()
                , node.NodeName
                , node.GetParentName()
                , partGeometry == null ? "N/A" : orgArea.ToString()
                , partGeometry == null ? "N/A" : orgVolume.ToString()
                , partGeometry == null ? "N/A" : resultArea.ToString()
                , partGeometry == null ? "N/A" : resultVolume.ToString()
                )
            );
    }
}


