// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    VIZCore3DX.NET.Data.Section section = vizcore3dx.Section.SelectedItem;

    if (section == null) return;

    if (section.SectionType == VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION)
    {
        VIZCore3DX.NET.Data.SectionPlaneEdgeCollection items
            = vizcore3dx.Section.GetClippedEdge(section.ID, -1);

        foreach (VIZCore3DX.NET.Data.SectionPlaneEdge edge in items)
        {
            System.Diagnostics.Trace.WriteLine(
                string.Format(
                    "P1:{0}, P2:{1}, C:{2}"
                    , edge.Postion1.ToString()
                    , edge.Postion2.ToString()
                    , edge.Color.ToArgb().ToString()
                    )
                    );
        }
    }
    else if (section.SectionType == VIZCore3DX.NET.Manager.SectionManager.SectionTypes.SECTION_BOX)
    {
        for (int i = 0; i < 6; i++)
        {
            VIZCore3DX.NET.Data.SectionPlaneEdgeCollection items
                = vizcore3dx.Section.GetClippedEdge(section.ID, i);

            foreach (VIZCore3DX.NET.Data.SectionPlaneEdge edge in items)
            {
                System.Diagnostics.Trace.WriteLine(
                    string.Format(
                        "P1:{0}, P2:{1}, C:{2}"
                        , edge.Postion1.ToString()
                        , edge.Postion2.ToString()
                        , edge.Color.ToArgb().ToString()
                        )
                        );
            }
        }
    }
}


