// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{       
    // Get Style
    VIZCore3DX.NET.Data.SectionStyle sectionStyle = vizcore3dx.Section.GetStyle();

    sectionStyle.BoundaryStrokeColor = Color.Red;
    sectionStyle.BoundaryStrokePattern = StrokePattern.Solid;
    sectionStyle.BoundaryStrokeThickness = 10;
    sectionStyle.DisabledBoundaryStrokeColor = Color.Red;
    sectionStyle.DisabledFaceFillColor = Color.Green;
    sectionStyle.FaceFillColor = Color.FromArgb(10, Color.Aqua);
    sectionStyle.HighlightedBoundaryStrokeColor = Color.Green;
    sectionStyle.IsBoxSectionManipulatorEnabled = true;
    sectionStyle.ManipulatorDisplayMode = SectionManipulatorDisplayMode.All;
    sectionStyle.SelectedBoundaryStrokeColor = Color.Green;

    // Set Style
    vizcore3dx.Section.SetStyle(sectionStyle);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{       
    // Get Style
    VIZCore3DX.NET.Data.SectionStyle sectionStyle = vizcore3dx.Section.GetStyle();

    sectionStyle.BoundaryStrokeColor = Color.Red;
    sectionStyle.BoundaryStrokePattern = StrokePattern.Solid;
    sectionStyle.BoundaryStrokeThickness = 10;
    sectionStyle.DisabledBoundaryStrokeColor = Color.Red;
    sectionStyle.DisabledFaceFillColor = Color.Green;
    sectionStyle.FaceFillColor = Color.FromArgb(10, Color.Aqua);
    sectionStyle.HighlightedBoundaryStrokeColor = Color.Green;
    sectionStyle.IsBoxSectionManipulatorEnabled = true;
    sectionStyle.ManipulatorDisplayMode = SectionManipulatorDisplayMode.All;
    sectionStyle.SelectedBoundaryStrokeColor = Color.Green;

    // Set Style
    vizcore3dx.Section.SetStyle(sectionStyle);   
}


private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void AddSelectionBox()
{
    string title = "Box #1";

    int id = vizcore3dx.SelectionBox.Add(
        new VIZCore3DX.NET.Data.Vertex3D(0, 0, 0)
        , new VIZCore3DX.NET.Data.Vertex3D(100, 100, 100)
        , Color.White
        , Color.Black
        , title
        );

    List<int> objects = 
        vizcore3dx.SelectionBox.GetObject3DIndex(
            id
            , VIZCore3DX.NET.Data.BoundBoxSearchOption.IncludingPart
            );
}


private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void AddSelectionBox()
{
    VIZCore3DX.NET.Data.BoundBox3D boundBox = vizcore3dx.Model.BoundBox;
    string title = "Box #1";

    int id = vizcore3dx.SelectionBox.Add(
        boundBox
        , Color.White
        , Color.Black
        , title
        );

    vizcore3dx.SelectionBox.Select(id);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int boxId = 1; /* Selection Box ID */

    vizcore3dx.SelectionBox.Select(boxId); /* Select */
    vizcore3dx.SelectionBox.Focus(boxId);  /* Focus */

    // Control Resize Controller
    vizcore3dx.SelectionBox.EnableController(
        true    /* X Axis. */ 
        , true  /* Y Axis. */
        , false /* Z Axis. */
        );

    // Get Status
    VIZCore3DX.NET.Data.SelectionBoxController status
        = vizcore3dx.SelectionBox.GetControllerStatus();

    bool x = status.XAxis;
    bool y = status.YAxis;
    bool z = status.ZAxis;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int boxId = 1; /* Selection Box ID */

    vizcore3dx.SelectionBox.Select(boxId); /* Select */
    vizcore3dx.SelectionBox.Focus(boxId);  /* Focus */

    // Control Resize Controller
    vizcore3dx.SelectionBox.EnableController(
        true    /* X Axis. */ 
        , true  /* Y Axis. */
        , false /* Z Axis. */
        );

    // Get Status
    VIZCore3DX.NET.Data.SelectionBoxController status
        = vizcore3dx.SelectionBox.GetControllerStatus();

    bool x = status.XAxis;
    bool y = status.YAxis;
    bool z = status.ZAxis;
