private void Example()
{
    int groupId = 1;
    float thickness = 3.0f;

    vizcore3dx.BeginUpdate();

    int shapeId = vizcore3dx.ShapeDrawing.AddLine(
            GetVertexList()
            , groupId
            , System.Drawing.Color.Red
            , thickness
            , true
            );

    vizcore3dx.EndUpdate();
}

private List<VIZCore3DX.NET.Data.Vertex3DItemCollection> GetVertexList()
{
    List<VIZCore3DX.NET.Data.Vertex3DItemCollection> vertex =
        new List<VIZCore3DX.NET.Data.Vertex3DItemCollection>();

    VIZCore3DX.NET.Data.BoundBox3D boundbox =
        vizcore3dx.Object3D.GeometryProperty.FromSelectedObject3D(false).GetBoundBox();

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MinY, boundbox.MinZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MaxY, boundbox.MinZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MaxY, boundbox.MinZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MaxY, boundbox.MinZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MaxY, boundbox.MinZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MinY, boundbox.MinZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MinY, boundbox.MinZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MinY, boundbox.MinZ));

        vertex.Add(item);
    }

    {
