        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MinY, boundbox.MaxZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
