        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MaxY, boundbox.MaxZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MinY, boundbox.MaxZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MinY, boundbox.MaxZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MinY, boundbox.MaxZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MaxY, boundbox.MinZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MinX, boundbox.MaxY, boundbox.MaxZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MaxY, boundbox.MinZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MaxY, boundbox.MaxZ));

        vertex.Add(item);
    }

    {
        VIZCore3DX.NET.Data.Vertex3DItemCollection item =
            new VIZCore3DX.NET.Data.Vertex3DItemCollection();

        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MinY, boundbox.MaxZ));
        item.Add(new VIZCore3DX.NET.Data.Vertex3D(boundbox.MaxX, boundbox.MinY, boundbox.MinZ));

        vertex.Add(item);
    }

    return vertex;
}


