// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();

    Data.BoundBox3D boundbox = vizcore3dx.Model.BoundBox;

    // SEA(바다) 커스텀 개체 추가
    int customSeaId = vizcore3dx.ShapeDrawing.AddSea(
        boundbox.MinZ - 10.0f   /* 높이 : Z축 */
        , true                  /* Visible */
        );

    // 기본 리소스 (SEA Material) 복사
    int material = vizcore3dx.Object3D.Material.Copy(Data.ResourceMaterials.SEA);
    System.Drawing.Color color = System.Drawing.Color.FromArgb(127, 255, 255, 255);

    // 리소스 색상 변경
    vizcore3dx.Object3D.Material.SetColor(material, color);

    // 수정된 리소스 (Material) 를 기존 커스텀 개체에 지정 
    vizcore3dx.ShapeDrawing.SetMaterial(customSeaId, material);

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.BeginUpdate();

    string imagePath = "C:\\Material\\Texture\\image.jpg";
    int material = vizcore3dx.Object3D.Material.Add(imagePath);

    int id = vizcore3dx.ShapeDrawing.AddCube(...);

    vizcore3dx.ShapeDrawing.SetMaterial(id, material);

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // 기존 단축키 설정 제거
    vizcore3dx.Shortcuts.Disable(Data.ShortcutFunctions.CONTROL_SELECT_REVIEW_DRAG);

    // 신규 단축키 설정
    bool result = vizcore3dx.Shortcuts.Update(
        Data.ShortcutFunctions.CONTROL_SELECT_REVIEW_DRAG
        , Keys.X
        , true
        , true
        , false
        );

    if (result == true)
    {
        MessageBox.Show("Shortcut Registered.", "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Information);
    }
    else
    {
        MessageBox.Show("NG.", "VIZCore3DX.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
    }
}


