// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private System.Timers.Timer timerScrew;

private void Example()
{
    timerScrew = new System.Timers.Timer();
    timerScrew.Interval = 100;
    timerScrew.Elapsed += TimerScrew_Elapsed;

    AnimationScrew(true);
}

private List<VIZCore3DX.NET.Data.Node> NODES_SCREW1 { get; set; }
private List<VIZCore3DX.NET.Data.Node> NODES_SCREW2 { get; set; }
private float Screw_Rotate_Angle = 0;

private void AnimationScrew(bool enable)
{
    if (enable == true)
    {
        NODES_SCREW1 = vizcore3dx.Object3D.Find.QuickSearch("SHIP_SCREW#1", true);
        NODES_SCREW2 = vizcore3dx.Object3D.Find.QuickSearch("SHIP_SCREW#2", true);

        timerScrew.Enabled = true;
    }
    else
    {
        timerScrew.Enabled = false;
    }
}

private void TimerScrew_Elapsed(object sender, System.Timers.ElapsedEventArgs e)
{
    timerScrew.Enabled = false;

    this.Invoke(new EventHandler(delegate
    {
        RotateScrew();
    }));

    timerScrew.Enabled = true;
}

private void RotateScrew()
{
    vizcore3dx.BeginUpdate();

    vizcore3dx.Object3D.Transform.Rotate(
        NODES_SCREW1
        , new VIZCore3DX.NET.Data.Vertex3D(-40144.21, 2159.66, -15277.70)
        , new VIZCore3DX.NET.Data.Vertex3D(-38685.59, 2170.91, -15204.43)
        , Screw_Rotate_Angle % 360
        , VIZCore3DX.NET.Data.AngleFormat.DEGREE
        );

    vizcore3dx.Object3D.Transform.Rotate(
        NODES_SCREW2
        , new VIZCore3DX.NET.Data.Vertex3D(-40110.58, -2575.70, -15209.66)
        , new VIZCore3DX.NET.Data.Vertex3D(-38651.92, -2564.30, -15135.98)
        , Screw_Rotate_Angle % 360
        , VIZCore3DX.NET.Data.AngleFormat.DEGREE
        );

    Screw_Rotate_Angle += 30;

    vizcore3dx.EndUpdate();
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    OpenFileDialog dlg = new OpenFileDialog();
    dlg.Filter = vizcore3dx.Model.OpenFilter;
    dlg.Multiselect = false;

    if (dlg.ShowDialog() != DialogResult.OK) return;

    if (vizcore3dx.Model.IsOpen() == true)
        vizcore3dx.Model.Close();

    // File Path
    string path = dlg.FileName;

    // File Stream
    VIZCore3DX.NET.Data.StreamData s1 = new VIZCore3DX.NET.Data.StreamData(System.IO.File.ReadAllBytes(path), "MODEL1");
    VIZCore3DX.NET.Data.StreamData s2 = new VIZCore3DX.NET.Data.StreamData(System.IO.File.ReadAllBytes(path), "MODEL2");

    List<VIZCore3DX.NET.Data.StreamData> stream = new List<VIZCore3DX.NET.Data.StreamData>();
    stream.Add(s1);
    stream.Add(s2);

    // Add Stream
    vizcore3dx.Model.AddStream(stream);

    // Model BoundBox
    VIZCore3DX.NET.Data.BoundBox3D boundBox = vizcore3dx.Model.BoundBox;

    // Find Object
    List<VIZCore3DX.NET.Data.Node> node = vizcore3dx.Object3D.Find.QuickSearch("MODEL2", true);

    // Move Object
    VIZCore3DX.NET.Data.Matrix3D matrix = new VIZCore3DX.NET.Data.Matrix3D();
    matrix.SetTranslate(boundBox.LengthX * 3.0f, 0.0f, 0.0f);
    node[0].Transform(matrix);

    // Set Scale : 2x
    vizcore3dx.Object3D.Transform.Scale(node, new VIZCore3DX.NET.Data.Vector3D(2.0f, 2.0f, 2.0f));
}


