// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<Data.SnapshotItem> snapshots = vizcore3dx.Snapshot.Snapshots;
    if (snapshots.Count == 0) return;
    Data.SnapshotItem item = snapshots[0];
    Data.SnapshotItem clone = vizcore3dx.Snapshot.Clone(item, "CloneSnapshot");
    clone.Restore();
}

