private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
   List<Data.Node> nodes = vizcore3dx.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);
   if (nodes.Count > 0)
   {
       List<Data.VertexColorData> vertexColorDatas = vizcore3dx.View.VertexColor.GetColor(nodes[0].Index);

       for (int i = 0; i < vertexColorDatas.Count; i++)
       {
            vertexColorDatas[i].color = Color.White;
       }

       vizcore3dx.View.VertexColor.SetColor(nodes[0].Index, vertexColorDatas);
       vizcore3dx.View.VertexColor.Enable = true;
   } 
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<VIZCore3DX.NET.Data.Node> nodes 
        = vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP);
    if (nodes.Count == 0) return;

    if (nodes[0].Kind != Data.NodeKind.PART) return;

    List<VIZCore3DX.NET.Data.VertexColorData> vertexColor 
        = vizcore3dx.View.VertexColor.GetColor(nodes[0].Index);

    VIZCore3DX.NET.Utility.ColorBrewerPalettesHelper palettes 
        = new VIZCore3DX.NET.Utility.ColorBrewerPalettesHelper();
    palettes.SetPalette(VIZCore3DX.NET.Utility.ColorBrewerPalettesHelper.Kind.Sequential_BuPu);

    for (int i = 0; i < vertexColor.Count; i++)
    {
        vertexColor[i].color = palettes.GetPaletteColor(i);
    }

    vizcore3dx.View.VertexColor.SetColor(nodes[0].Index, vertexColor);
    vizcore3dx.View.VertexColor.Enable = true;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    List<VIZCore3DX.NET.Data.Node> nodes 
        = vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP);

    if (nodes.Count == 0) return;

    if (nodes[0].Kind != Data.NodeKind.PART) return;

    int vertexCount = vizcore3dx.View.VertexColor.GetColor(nodes[0].Index).Count;
    List<System.Drawing.Color> vertexColor = new List<System.Drawing.Color>();

    VIZCore3DX.NET.Utility.ColorBrewerPalettesHelper palettes 
        = new VIZCore3DX.NET.Utility.ColorBrewerPalettesHelper();
    palettes.SetPalette(VIZCore3DX.NET.Utility.ColorBrewerPalettesHelper.Kind.Sequential_BuPu);

    for (int i = 0; i < vertexCount; i++)
    {
        vertexColor.Add(palettes.GetPaletteColor(i));
    }

    vizcore3dx.View.VertexColor.SetColor(nodes[0].Index, vertexColor);
    vizcore3dx.View.VertexColor.Enable = true;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    vizcore3dx.View.OnViewContextMenuOpeningEvent += View_OnViewContextMenuOpeningEvent;
}

private void View_OnViewContextMenuOpeningEvent(object sender, Event.EventManager.ViewContextMenuEventArgs e)
{
    // 노드 단일 선택 Context Menu 인 경우만 활성화 허용
    if (e.Kind == Data.ViewContextMenuKind.NODE_SINGLE)
        e.Cancel = false;
    // 다른 Context Menu는 사용안함
    else
        e.Cancel = true;
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // 기능 활성화
    vizcore3dx.View.PreSelect.Enable = true;

    // 하이라이트 색상 설정
    vizcore3dx.View.PreSelect.HighlightColor = System.Drawing.Color.Lime;

    // Label 유형 지정
    vizcore3dx.View.PreSelect.Label = VIZCore3DX.NET.Data.PreSelectStyle.LabelKind.HIERACHY_TOP_DOWN;

    // Label Font 설정
    vizcore3dx.View.PreSelect.LabelFont = new System.Drawing.Font("Arial", 10.0f);

    // 이벤트 연동
    vizcore3dx.View.OnViewPreSelectLabelEvent += View_OnViewPreSelectLabelEvent;
}

private void View_OnViewPreSelectLabelEvent(object sender, Event.EventManager.ViewPreSelectLabelEventArgs e)
{
    // 내부 Label 처리 취소
    // 내부 Label을 사용하려면, 이벤트 연동을 하지 않거나, e.Cancel = false; 지정
    e.Cancel = true;

    VIZCore3DX.NET.Data.Node node 
        = vizcore3dx.Object3D.FromIndex(e.NodeIndex);

    // PreSelect Label 에 표시할 문자열 지정
    e.Label = string.Format("Custom Tooltip : {0}", node.NodeName);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Event 선언
    vizcore3dx.View.OnViewDragDrop += View_OnViewDragDrop;
}

private void View_OnViewDragDrop(object sender, Event.EventManager.ViewDragDropEventArgs e)
{
    int x = e.X;
    int y = e.Y;

    // 해당 위치 개체 조회
    int index = vizcore3dx.View.HitTest(x, y);
    if(index != -1)
    {
        VIZCore3DX.NET.Data.Node node = vizcore3dx.Object3D.FromIndex(index);
    }

    // 해당 위치 선택상자(SelectionBox) 조회
    int id = vizcore3dx.SelectionBox.HitTest(x, y);
    if(id != -1)
    {
        VIZCore3DX.NET.Data.SelectionBox box = vizcore3dx.SelectionBox.GetItem(id);
    }

    // Drag Item
    if(e.DragItem != null)
    {
        // Example : ListViewItem lvi = (ListViewItem)e.DragItem;
    }
}


