    vizcore3dx.View.BoxZoom(new Data.Vector2(296, 288), new Data.Vector2(344, 327), TimeSpan.FromSeconds(1));
}

private void Example2()
{
    vizcore3dx.View.BoxZoom(296, 288, 344, 327, TimeSpan.FromSeconds(1));
}


