// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    // Camera Position
    VIZCore3DX.NET.Data.Vertex3D camera = new Data.Vertex3D(18200.0f, 4095.0f, 17354.0f);

    vizcore3dx.View.Projection = Data.Projections.Perspective;
    vizcore3dx.View.FOV = Convert.ToSingle(60.0f);
    vizcore3dx.View.SetPivotPosition(camera);
    vizcore3dx.Walkthrough.LockZAxis = false;
    vizcore3dx.View.MoveCamera(camera, 0.0f, false);

    float degree = 5.0f;

    // Camera Direction
    // Up(0), Down(1), Left(2), Right(3)
    int cameraDir = 0;

    if(cameraDir == 0)
        vizcore3dx.View.RotateCameraByAxis(Data.BaseAxis.LOCAL, Data.Axis.X, Data.Directions.PLUS, degree);
    else if(cameraDir == 1)
        vizcore3dx.View.RotateCameraByAxis(Data.BaseAxis.LOCAL, Data.Axis.X, Data.Directions.MINUS, degree);
    else if (cameraDir == 2)
        vizcore3dx.View.RotateCameraByAxis(Data.BaseAxis.WORLD, Data.Axis.Z, Data.Directions.PLUS, degree);
    else if (cameraDir == 3)
        vizcore3dx.View.RotateCameraByAxis(Data.BaseAxis.WORLD, Data.Axis.Z, Data.Directions.MINUS, degree);
}


