    // 화면에 가시화하지 않고, 메모리 상태에서 렌더링 및 화면 이미지 반환
    vizcore3dx.View.MoveCamera(VIZCore3DX.NET.Data.CameraDirection.ISO_PLUS);
    System.Drawing.Image img1 = vizcore3dx.View.GetBackgroundRenderingImage();
    vizcore3dx.View.MoveCamera(VIZCore3DX.NET.Data.CameraDirection.ISO_MINUS);
    System.Drawing.Image img2 = vizcore3dx.View.GetBackgroundRenderingImage();

    vizcore3dx.View.MoveCamera(VIZCore3DX.NET.Data.CameraDirection.X_PLUS);
    System.Drawing.Image img3 = vizcore3dx.View.GetBackgroundRenderingImage();
    vizcore3dx.View.MoveCamera(VIZCore3DX.NET.Data.CameraDirection.X_MINUS);
    System.Drawing.Image img4 = vizcore3dx.View.GetBackgroundRenderingImage();

    vizcore3dx.View.MoveCamera(VIZCore3DX.NET.Data.CameraDirection.Y_PLUS);
    System.Drawing.Image img5 = vizcore3dx.View.GetBackgroundRenderingImage();
    vizcore3dx.View.MoveCamera(VIZCore3DX.NET.Data.CameraDirection.Y_MINUS);
    System.Drawing.Image img6 = vizcore3dx.View.GetBackgroundRenderingImage();

