// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;

    List<int> index = new List<int>() { 100, 200, 300 };

    System.Drawing.Image img = vizcore3dx.View.GetImage(
        index   /* Node Index */
        , 800   /* Width */
        , 600   /* Height */
        );
    if (img == null) return;

    SaveFileDialog dlg = new SaveFileDialog();
    dlg.Filter = "Image (.png)|*.png";
    if (dlg.ShowDialog() != DialogResult.OK) return;

    img.Save(dlg.FileName);

    VIZCore3DX.NET.Utility.ExplorerHelper.Show(dlg.FileName);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.Model.IsOpen() == false) return;
    if (vizcore3dx.Object3D.FromFilter(VIZCore3DX.NET.Data.Object3dFilter.SELECTED_TOP).Count == 0) return;

    System.Drawing.Image img = vizcore3dx.View.GetImage(
        800     /* Width */
        , 600   /* Height */
        );
    if (img == null) return;

    SaveFileDialog dlg = new SaveFileDialog();
    dlg.Filter = "Image (.png)|*.png";
    if (dlg.ShowDialog() != DialogResult.OK) return;

    img.Save(dlg.FileName);

    VIZCore3DX.NET.Utility.ExplorerHelper.Show(dlg.FileName);
}


// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    int x = 300;
    int y = 400;

    VIZCore3DX.NET.Data.PickedSurfaceData vector = 
        vizcore3dx.View.GetPickedSurfaceNormalVector(x, y);

    // 선택된 부재가 없음
    if (vector.Picked == false) return;

    // 표면점이 없음
    if (vector.Surface == false) return;

    // 선택된 지점
    VIZCore3DX.NET.Data.Vertex3D position = vector.Position;

    // 선택된 지점의 Normal Vector
    VIZCore3DX.NET.Data.Vector3D normal = vector.Normal;
}


