// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example()
{
    if (vizcore3dx.View.Navigation != VIZCore3DX.NET.Data.NavigationModes.WALK) return;
    if (vizcore3dx.Walkthrough.Avatar == false) return;

    if (vizcore3dx.Walkthrough.DetectAvatarCollision() == false) return;

    List<VIZCore3DX.NET.Data.AvatarCollision> items =
        vizcore3dx.Walkthrough.GetAvatarCollision();

    foreach (VIZCore3DX.NET.Data.AvatarCollision item in items)
    {
        int index = item.NodeIndex;
        VIZCore3DX.NET.Data.Vertex3D v = item.Position;
    }
}


