// VIZCore3DX.NET Control
private VIZCore3DX.NET.VIZCore3DXControl vizcore3dx;

private void Example1()
{
    string assemblyPath = VIZCore3DX.NET.ModuleInitializer.GetAssemblyLocation();
    string entryAssemblyPath = VIZCore3DX.NET.ModuleInitializer.GetEntryAssemblyLocation();

    string assemblyVersion = VIZCore3DX.NET.ModuleInitializer.GetLibraryVersion();
    string interopVersion = VIZCore3DX.NET.ModuleInitializer.GetInteropLibraryVersion();
    bool compareVersion = VIZCore3DX.NET.ModuleInitializer.CompareVersion();

    bool existLibrary = VIZCore3DX.NET.ModuleInitializer.ExistLibrary();

    // Initialize VIZCore3DX.NET
    // Extract Library by Condition
    if (existLibrary == false || compareVersion == false)
        VIZCore3DX.NET.ModuleInitializer.Run();
}

private void Example2()
{
    // Initialize VIZCore3DX.NET
    // Extract Library Always
    VIZCore3DX.NET.ModuleInitializer.Run();
}


